import React from "react";
import { useSelector } from "react-redux";

const Body = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  console.log("user.retObj.userDetails.maGv", user.retObj.userDetails.username);
  const teachers = useSelector((state) => state.admin.allTeacher);
  const departments = useSelector((state) => state.admin.allDepartment);

  // if (teachers && departments) {
  //   const teacherObj = teachers.find(
  //     (dp) => dp.maGv === user.retObj.userDetails.username
  //   );
  //   const departmentObj = departments.find(
  //     (dp) => dp.maKhoa === teacherObj.maKhoa
  //   );
  // }
  const store = useSelector((state) => state);

  const getTeacherAndDepartment = async () => {
    try {
      let teachers = store.admin.allTeacher;
      let departments = store.admin.allDepartment;

      // Chờ đợi cho đến khi dữ liệu được cập nhật trong Redux store
      while (!teachers || !departments) {
        await new Promise((resolve) => setTimeout(resolve, 100)); // Chờ 100ms
        teachers = store.admin.allTeacher;
        departments = store.admin.allDepartment;
      }

      if (teachers && departments) {
        const teacherObj = teachers.find(
          (dp) => dp.maGv === user.retObj.userDetails.username
        );

        const departmentObj = departments.find(
          (dp) => dp.maKhoa === teacherObj.maKhoa
        );

        console.log("departmentObj", departmentObj);

        return departmentObj; // Trả về departmentObj
      }
    } catch (error) {
      console.error("Lỗi khi lấy dữ liệu giáo viên và khoa: ", error);
      throw error;
    }
  };

  // Sử dụng await khi gọi hàm getTeacherAndDepartment để đợi dữ liệu trả về
  const departmentObj = getTeacherAndDepartment();

  // Sau đó, bạn có thể sử dụng departmentObj cho các thao tác khác bên ngoài hàm
  console.log("departmentObj", departmentObj.PromiseResult);

  return (
    // <div className="flex-[0.8] mt-3 mx-5 item-center">
    //   <p className="block text-black ">Thông tin khoa</p>

    //   <div className="items-center justify-center w-full mt-5">
    //     <table className="w-full table-auto ">
    //       <thead className="bg-[#E1EEEE] items-center">
    //         <tr>
    //           <th className="px-4 py-2">Mã Khoa</th>
    //           <th className="px-4 py-2">Tên Khoa</th>
    //           <th className="px-4 py-2">SĐT</th>
    //           <th className="px-4 py-2">Email</th>
    //         </tr>
    //       </thead>

    //       <tbody className="">
    //         <tr className="justify-center item-center hover:bg-[#EEF5F5]">
    //           <td className="px-4 py-2 border">{departmentObj.maKhoa}</td>
    //           <td className="px-4 py-2 border">{departmentObj.tenKhoa}</td>
    //           <td className="px-4 py-2 border">{departmentObj.sdt}</td>
    //           <td className="px-4 py-2 border">{departmentObj.email}</td>
    //         </tr>
    //       </tbody>
    //     </table>
    //   </div>
    // </div>
    <div className="flex items-center justify-center w-full mt-5">
      <table className="w-full table-auto">
        <thead className="bg-[#E1EEEE] items-center">
          <tr>
            <th className="px-4 py-2">Mã Khoa</th>
            <th className="px-4 py-2">Tên Khoa</th>
            <th className="px-4 py-2">SĐT</th>
            <th className="px-4 py-2">Email</th>
          </tr>
        </thead>

        <tbody className="">
          <tr className="justify-center item-center hover:bg-[#EEF5F5]">
            <td className="px-4 py-2 border">{departmentObj.maKhoa}</td>
            <td className="px-4 py-2 border">{departmentObj.tenKhoa}</td>
            <td className="px-4 py-2 border">{departmentObj.sdt}</td>
            <td className="px-4 py-2 border">{departmentObj.email}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};
export default Body;
