import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  deleteCourse,
  getCourseUnit,
  updateCourse,
} from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import {
  DELETE_COURSE,
  SET_ERRORS,
  UPDATE_COURSE,
} from "../../../redux/actionTypes";
import { Link } from "react-router-dom";
import { format } from "date-fns";
import ReactModal from "react-modal";
import CourseDetail from "../getCourseDetail/CourseDetail";
import Swal from "sweetalert2";
const modalStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "0",
  },
};

// http://localhost:9090/api/admin/dsLopTc/lop/CNTT01
const Body = () => {
  const dispatch = useDispatch();
  const [unit, setUnit] = useState("");
  const [error, setError] = useState({});
  const units = useSelector((state) => state.admin.allUnit);
  const subjects = useSelector((state) => state.admin.allSubject);
  const teachers = useSelector((state) => state.admin.allTeacher);

  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const [modalMode, setModalMode] = useState(null);

  const store = useSelector((state) => state);

  const UnitObj = units?.find((dp) => dp.tenLop === unit);
  const UnitId = UnitObj?.maLop;

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    const UnitObj = units.find((dp) => dp.tenLop === unit);
    const UnitId = UnitObj?.maLop;
    dispatch(getCourseUnit(UnitId));
  };

  const courses = useSelector((state) => state.admin.courses.retObj);

  useEffect(() => {
    if (courses?.length !== 0) {
      setLoading(false);
    }
  }, [courses]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  // BEGIN EDIT
  const [inputType, setInputType] = useState("text");
  const [value, setValue] = useState({
    id: "",
    maLopTc: "",
    nienKhoa: "",
    ky: "",
    timeBd: "",
    timeKt: "",
    soLuong: "",
    soLuongCon: "",
    maMh: "",
    maGv: "",
    maLop: "",
  });
  const [selectedCourse, setSelectedCourse] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };
  const handleEditClick = (course) => {
    setSelectedCourse(course);
    setIsModalOpen(true);
    setModalMode("edit");
    setValue({
      id: course.id,
      maLopTc: course.maLopTc,
      nienKhoa: "",
      ky: "",
      timeBd: "",
      timeKt: "",
      soLuong: "",
      soLuongCon: course.soLuongCon,
      maMh: "",
      maGv: "",
      maLop: "",
    });
  };
  const handleFormSubmit = (e) => {
    e.preventDefault();
    const updatedValue = {};

    if (value.nienKhoa !== "") {
      updatedValue.nienKhoa = value.nienKhoa;
    } else {
      updatedValue.nienKhoa = selectedCourse.nienKhoa;
    }
    if (value.ky !== "") {
      updatedValue.ky = value.ky;
    } else {
      updatedValue.ky = selectedCourse.ky;
    }
    if (value.timeBd !== "") {
      updatedValue.timeBd = value.timeBd;
    } else {
      updatedValue.timeBd = selectedCourse.timeBd;
    }
    if (value.timeKt !== "") {
      updatedValue.timeKt = value.timeKt;
    } else {
      updatedValue.timeKt = selectedCourse.timeKt;
    }
    if (value.soLuong !== "") {
      updatedValue.soLuong = value.soLuong;
    } else {
      updatedValue.soLuong = selectedCourse.soLuong;
    }
    if (value.maMh !== "") {
      updatedValue.maMh = value.maMh;
    } else {
      updatedValue.maMh = selectedCourse.maMh;
    }
    if (value.maGv !== "") {
      updatedValue.maGv = value.maGv;
    } else {
      updatedValue.maGv = selectedCourse.maGv;
    }
    if (value.maLop !== "") {
      updatedValue.maLop = value.maLop;
    } else {
      updatedValue.maLop = selectedCourse.maLop;
    }

    dispatch(updateCourse({ ...selectedCourse, ...updatedValue }));
    dispatch({ type: UPDATE_COURSE, payload: false });
    closeModal();
  };

  useEffect(() => {
    dispatch(getCourseUnit(selectedCourse.maLop));
  }, [dispatch, store.errors, store.admin.updatedCourse]);

  //END EDIT
  //VIEW
  const handleOpenViewModal = (course) => {
    setSelectedCourse(course);
    setModalMode("view");
    setIsModalOpen(true);
  };
  //END VIEW
  // BEGIN DELETE
  const [checkedValue, setCheckedValue] = useState([]);
  // check xóa
  const handleInputChange = (e) => {
    const value = e.target.value;
    const isChecked = e.target.checked;
    setCheckedValue((prevState) =>
      isChecked
        ? [...prevState, value]
        : prevState.filter((item) => item !== value)
    );
  };
  // hàm xóa

  const dltSubject = (e) => {
    Swal.fire({
      title: "Are you sure?",
      text: "You won't be able to revert this!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Yes, delete it!",
    }).then(async (result) => {
      if (result.isConfirmed) {
        dispatch(deleteCourse(checkedValue));
      }
    });
  };
  // câp nhật lại
  useEffect(() => {
    if (store.admin.courseDeleted) {
      setCheckedValue([]);
      const UnitObj = units.find((dp) => dp.tenLop === unit);
      const UnitId = UnitObj.maLop;
      dispatch(getCourseUnit(UnitId));
      dispatch({ type: DELETE_COURSE, payload: false });
    }
  }, [store.admin.courseDeleted]);
  // END DELETE
  useEffect(() => {
    dispatch(getCourseUnit(UnitId));
  }, [store.errors]);

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Danh sách Lớp Tín chỉ</p>

      <div className="flex mt-4">
        <Link to="/admin/addcourse" className="btn btn-primary">
          <button
            className="items-center gap-[9px] mr-4 w-[88px] h-[53px] hover:bg-[#04605E] block py-2 font-bold text-white rounded-lg px-4 
           bg-[#157572] focus:outline-none focus:shadow-outline "
          >
            Thêm
          </button>
        </Link>
        {courses && (
          <button
            onClick={dltSubject}
            className={
              "items-center gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4" +
              (checkedValue && checkedValue.length
                ? " hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
                : "")
            }
            disabled={!(courses && checkedValue?.length > 0)}
          >
            Xóa
          </button>
        )}
        {/* <button
          onClick={dltSubject}
          className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
        >
          Xóa
        </button> */}
      </div>

      {/* ô search */}
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Lớp</label>

          <div className="flex">
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={unit}
              onChange={(e) => setUnit(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {units?.map((ut, idx) => (
                <MenuItem key={idx} value={ut.tenLop}>
                  {ut.tenLop}
                </MenuItem>
              ))}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>

      <div className="w-full">
        <div className="col-span-3">
          <div className={classes.loadingAndError}>
            {loading && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#157572"
                messageColor="#157572"
              />
            )}
            {/* chỗ này tạm thời để vậy đã */}
            {courses?.length === 0 && (
              <p className="text-2xl font-bold text-red-500">
                Lớp chưa có Lớp tín chỉ
              </p>
            )}
          </div>

          {search &&
            !loading &&
            // Object.keys(error).length === 0 &&
            courses?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">Chọn</th>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">Mã Lớp Tín Chỉ</th>
                    <th className="px-4 py-2">Niên Khóa</th>
                    <th className="px-4 py-2">Kỳ</th>

                    <th className="px-4 py-2">Thời Gian Bắt Đầu</th>
                    <th className="px-4 py-2">Thời Gian Kết Thúc</th>
                    <th className="px-4 py-2">Số lượng</th>
                    <th className="px-4 py-2">Số lượng còn</th>

                    <th className="px-4 py-2">Mã Môn Học</th>
                    <th className="px-4 py-2">Mã Giảng Viên</th>
                    <th className="px-4 py-2">Hành Động</th>
                  </tr>
                </thead>
                <tbody className="">
                  {courses?.map((course, idx) => (
                    // <tr
                    //   className="justify-center item-center hover:bg-[#EEF5F5]"
                    //   key={idx}
                    // >
                    //   <td className="px-4 py-2 border ">
                    //     <input
                    //       onChange={handleInputChange}
                    //       checked={checkedValue.includes(course.id)}
                    //       value={course.id}
                    //       type="checkbox"
                    //     />
                    //   </td>
                    //   <td className="px-4 py-2 border">{idx + 1}</td>

                    //   <td className="px-4 py-2 border">{course.maLopTc}</td>
                    //   <td className="px-4 py-2 border">{course.nienKhoa}</td>
                    //   <td className="px-4 py-2 border">{course.ky}</td>

                    //   <td className="px-4 py-2 border">
                    //     {format(new Date(course.timeBd), "dd/MM/yyyy")}
                    //   </td>

                    //   <td className="px-4 py-2 border">
                    //     {" "}
                    //     {format(new Date(course.timeKt), "dd/MM/yyyy")}
                    //   </td>
                    //   <td className="px-4 py-2 border">{course.soLuong}</td>
                    //   <td className="px-4 py-2 border">{course.soLuongCon}</td>

                    //   <td className="px-4 py-2 border">{course.maMh}</td>
                    //   <td className="px-4 py-2 border">{course.maGv}</td>
                    //   <td className="flex items-center justify-center flex-1 flex-grow w-full h-full px-4 py-2 mr-0 border flex-nowrap gap-y-1 gap-x-1">
                    //     <button
                    //       className="w-full  whitespace-nowrap h-full px-3 py-1  font-bold text-white rounded hover:bg-[#04605E] bg-[#157572]  focus:outline-none focus:shadow-outline"
                    //       onClick={() => handleOpenViewModal(course)}
                    //     >
                    //       Chi tiết
                    //     </button>
                    //     {modalMode === "view" && (
                    //       <CourseDetail
                    //         isOpen={isModalOpen}
                    //         onClose={closeModal}
                    //         course={selectedCourse}
                    //       />
                    //     )}
                    //     <button
                    //       className="w-full h-full px-3 py-1 font-bold text-white rounded  hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                    //       onClick={() => handleEditClick(course)}
                    //     >
                    //       Sửa
                    //     </button>
                    //   </td>
                    // </tr>
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border">
                        <input
                          onChange={handleInputChange}
                          checked={checkedValue.includes(course.id)}
                          value={course.id}
                          type="checkbox"
                        />
                      </td>
                      <td className="px-4 py-2 border">{idx + 1}</td>
                      <td className="px-4 py-2 border">{course.maLopTc}</td>
                      <td className="px-4 py-2 border">{course.nienKhoa}</td>
                      <td className="px-4 py-2 border">{course.ky}</td>
                      <td className="px-4 py-2 border">
                        {format(new Date(course.timeBd), "dd/MM/yyyy")}
                      </td>
                      <td className="px-4 py-2 border">
                        {format(new Date(course.timeKt), "dd/MM/yyyy")}
                      </td>
                      <td className="px-4 py-2 border">{course.soLuong}</td>
                      <td className="px-4 py-2 border">{course.soLuongCon}</td>
                      <td className="px-4 py-2 border">{course.maMh}</td>
                      <td className="px-4 py-2 border">{course.maGv}</td>
                      <td className="px-4 py-2 border">
                        <div className="flex flex-col items-center justify-center h-full gap-y-1">
                          <button
                            className="w-full whitespace-nowrap h-full px-3 py-1 font-bold text-white rounded hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                            onClick={() => handleOpenViewModal(course)}
                          >
                            Chi tiết
                          </button>
                          {modalMode === "view" && (
                            <CourseDetail
                              isOpen={isModalOpen}
                              onClose={closeModal}
                              course={selectedCourse}
                            />
                          )}
                          <button
                            className="w-full h-full px-3 py-1 font-bold text-white rounded hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                            onClick={() => handleEditClick(course)}
                          >
                            Sửa
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
      {modalMode === "edit" && (
        <ReactModal
          isOpen={isModalOpen}
          onRequestClose={openModal}
          style={modalStyles}
          ariaHideApp={false}
        >
          <div className={classes.Form1}>
            <form className={classes.Form2} onSubmit={handleFormSubmit}>
              {/* item */}
              <div className={classes.FormItem}>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Mã lớp tín chỉ:</h1>
                  <input
                    placeholder={selectedCourse.maLopTc}
                    disabled
                    className={classes.InputStyle}
                    type="text"
                  />
                </div>

                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}> Niên khóa:</h1>
                  <input
                    placeholder={selectedCourse.nienKhoa}
                    className={classes.InputStyle}
                    type="text"
                    value={value.nienKhoa}
                    onChange={(e) =>
                      setValue({ ...value, nienKhoa: e.target.value })
                    }
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}> Kỳ:</h1>
                  <input
                    placeholder={selectedCourse.ky}
                    className={classes.InputStyle}
                    type="text"
                    value={value.ky}
                    onChange={(e) => setValue({ ...value, ky: e.target.value })}
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Thời gian bắt đầu :</h1>

                  <input
                    placeholder={format(
                      new Date(selectedCourse.timeBd),
                      "MM/dd/yyyy"
                    )}
                    className={classes.InputStyle}
                    type={inputType}
                    value={value.timeBd}
                    onChange={(e) =>
                      setValue({ ...value, timeBd: e.target.value })
                    }
                    onFocus={() => setInputType("date")}
                    onBlur={() => setInputType("text")}
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Thời gian kết thúc :</h1>

                  <input
                    placeholder={format(
                      new Date(selectedCourse.timeKt),
                      "MM/dd/yyyy"
                    )}
                    className={classes.InputStyle}
                    type={inputType}
                    value={value.timeKt}
                    onChange={(e) =>
                      setValue({ ...value, timeKt: e.target.value })
                    }
                    onFocus={() => setInputType("date")}
                    onBlur={() => setInputType("text")}
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Số lượng :</h1>
                  <input
                    placeholder={selectedCourse.soLuong}
                    className={classes.InputStyle}
                    type="number"
                    value={value.soLuong}
                    onChange={(e) =>
                      setValue({ ...value, soLuong: e.target.value })
                    }
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Số lượng còn:</h1>
                  <input
                    placeholder={selectedCourse.soLuongCon}
                    className={classes.InputStyle}
                    type="number"
                    value={value.soLuongCon}
                    disabled
                  />
                </div>

                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Môn học :</h1>
                  <Select
                    required
                    displayEmpty
                    sx={{
                      height: 36,
                      outline: "none",
                    }}
                    inputProps={{ "aria-label": "Without label" }}
                    // value={value.maLop}
                    value={value.maMh || selectedCourse.maMh}
                    onChange={(e) =>
                      setValue({ ...value, maMh: e.target.value })
                    }
                    className={`${classes.InputStyle} hover:focus:border-none `}
                  >
                    {/* <MenuItem value="">None</MenuItem> */}
                    {subjects?.map((dp, idx) => (
                      <MenuItem key={idx} value={dp.maMh}>
                        {dp.tenMh}
                      </MenuItem>
                    ))}
                  </Select>
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Lớp học :</h1>
                  <Select
                    required
                    displayEmpty
                    sx={{
                      height: 36,
                      outline: "none",
                    }}
                    inputProps={{ "aria-label": "Without label" }}
                    // value={value.maLop}
                    value={value.maLop || selectedCourse.maLop}
                    onChange={(e) =>
                      setValue({ ...value, maLop: e.target.value })
                    }
                    className={`${classes.InputStyle} hover:focus:border-none `}
                  >
                    {/* <MenuItem value="">None</MenuItem> */}
                    {units?.map((dp, idx) => (
                      <MenuItem key={idx} value={dp.maLop}>
                        {dp.tenLop}
                      </MenuItem>
                    ))}
                  </Select>
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Giảng viên :</h1>
                  <Select
                    required
                    displayEmpty
                    sx={{
                      height: 36,
                      outline: "none",
                    }}
                    inputProps={{ "aria-label": "Without label" }}
                    // value={value.maLop}
                    value={value.maGv || selectedCourse.maGv}
                    onChange={(e) =>
                      setValue({ ...value, maGv: e.target.value })
                    }
                    className={`${classes.InputStyle} hover:focus:border-none `}
                  >
                    {/* <MenuItem value="">None</MenuItem> */}
                    {teachers?.map((dp, idx) => (
                      <MenuItem key={idx} value={dp.maGv}>
                        {dp.ho} {dp.ten}
                      </MenuItem>
                    ))}
                  </Select>
                </div>
              </div>

              {/* buton */}
              <div className={classes.WrapButton}>
                <button className={classes.adminFormSubmitButton} type="submit">
                  Lưu
                </button>
                <button
                  className={classes.adminFormClearButton}
                  type="button"
                  onClick={closeModal}
                >
                  Hủy
                </button>
              </div>
              {/* ERROR */}
              <div className={classes.loadingAndError}>
                {loading && (
                  <Spinner
                    message="Updating"
                    height={30}
                    width={150}
                    color="#157572"
                    messageColor="#157572"
                  />
                )}
              </div>
            </form>
          </div>
        </ReactModal>
      )}
    </div>
  );
};

export default Body;
