import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllTKB } from "../../../redux/actions/adminActions";
import * as classes from "../../../utils/styles";
import { SET_ERRORS } from "../../../redux/actionTypes";
import Spinner from "../../../utils/Spinner";

const Body = () => {
  // const query = {
  //   timeInputBegin: "2023-02-20",
  //   timeInputEnd: "2023-04-30",
  // };
  const dispatch = useDispatch();
  const [error, setError] = useState({});
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);

  // dispatch(getAllTKB(query));
  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);
  const [inputType, setInputType] = useState("text");

  const [value, setValue] = useState({
    timeInputBegin: "",
    timeInputEnd: "",
  });
  console.log("value", value);
  const handleFormSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    dispatch(getAllTKB(value));
  };
  const allTKB = useSelector((state) => state.admin.allTKB.retObj);
  console.log("allTKB", allTKB);

  useEffect(() => {
    if (allTKB?.length !== 0) {
      setLoading(false);
    }
  }, [allTKB]);
  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);
  return (
    <div className="items-center justify-center">
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleFormSubmit}
        >
          <label htmlFor="department">Khoa</label>

          <div className="flex">
            <div className={classes.WrapInputLabel}>
              <h1 className={classes.LabelStyle}>thời gian bắt đầu :</h1>

              <input
                placeholder="Thời gian bắt đầu"
                className={classes.InputStyle}
                type={inputType}
                value={value.timeInputBegin}
                onChange={(e) =>
                  setValue({ ...value, timeInputBegin: e.target.value })
                }
                onFocus={() => setInputType("date")}
                onBlur={() => setInputType("text")}
              />
            </div>
            <div className={classes.WrapInputLabel}>
              <h1 className={classes.LabelStyle}>thời gian kết thúc :</h1>

              <input
                placeholder="Thời gian bắt đầu"
                className={classes.InputStyle}
                type={inputType}
                value={value.timeInputEnd}
                onChange={(e) =>
                  setValue({ ...value, timeInputEnd: e.target.value })
                }
                onFocus={() => setInputType("date")}
                onBlur={() => setInputType("text")}
              />
            </div>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>
      <div className="w-full">
        <div className="col-span-3">
          <div className={classes.loadingAndError}>
            {loading && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#111111"
                messageColor="blue"
              />
            )}
            {/* chỗ này tạm thời để vậy đã */}
            {allTKB?.length === 0 && (
              <p className="text-2xl font-bold text-red-500">
                giáo viên chưa có thời khó biểu
              </p>
            )}
          </div>

          {search &&
            !loading &&
            Object.keys(error).length === 0 &&
            allTKB?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">mã môn học</th>
                    <th className="px-4 py-2">Tiết bắt đầu</th>
                    <th className="px-4 py-2">Phòng học</th>
                  </tr>
                </thead>
                <tbody className="">
                  {allTKB?.map((teacher, idx) => (
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">{teacher.maMh}</td>
                      <td className="px-4 py-2 border">{teacher.tiet}</td>
                      <td className="px-4 py-2 border">{teacher.phong}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
    </div>
  );
};

export default Body;
