import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTeacherDepartment } from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import { SET_ERRORS } from "../../../redux/actionTypes";
import { Link } from "react-router-dom";
//http://localhost:9090/api/admin/giangVien/khoa/CNTT
const Body = () => {
  const dispatch = useDispatch();
  const [department, setDepartment] = useState("");
  const [error, setError] = useState({});
  const departments = useSelector((state) => state.admin.allDepartment);
  console.log("departments", departments);
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);
  const departmentObj = departments.find((dp) => dp.tenKhoa === department);
  console.log("departmentObj", departmentObj.maKhoa);
  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    console.log("department", department);
    // const departmentObj = departments.find((dp) => dp.tenKhoa === department);

    // const departmentId = departmentObj.maKhoa;
    // console.log("departmentId", departmentId);
    // dispatch(getTeacherDepartment(departmentId));
  };

  const teachers = useSelector((state) => state.admin.teachers.data);

  useEffect(() => {
    if (teachers?.length !== 0) {
      setLoading(false);
    }
  }, [teachers]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 flex flex-col ml-7">
      <div className="flex justify-between">
        <p className="block text-black ">Teachers List</p>
        <Link to="/admin/addteacher" className="btn btn-primary">
          <button className="block px-4 py-2 mr-10 font-bold text-white rounded bg-primary hover:bg-green-600 focus:outline-none focus:shadow-outline">
            Add Teacher
          </button>
        </Link>
      </div>

      <div className="flex my-8 ">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Department</label>
          <div className="flex">
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={department}
              onChange={(e) => setDepartment(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {departments?.map((dp, idx) => (
                <MenuItem key={idx} value={dp.tenKhoa}>
                  {dp.tenKhoa}
                </MenuItem>
              ))}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>

      <div className="w-full">
        <div className="col-span-3 mr-6">
          <div className={classes.loadingAndError}>
            {loading && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#111111"
                messageColor="blue"
              />
            )}
            {(error.noTeacherError || error.backendError) && (
              <p className="text-2xl font-bold text-red-500">
                {error.noTeacherError || error.backendError}
              </p>
            )}
          </div>

          {search &&
            !loading &&
            Object.keys(error).length === 0 &&
            teachers?.length !== 0 && (
              <table className="w-full table-auto">
                <thead>
                  <tr>
                    <th className="px-4 py-2">Sr no.</th>
                    <th className="px-4 py-2">Teacher Code</th>
                    <th className="px-4 py-2">Last name</th>
                    <th className="px-4 py-2">First name</th>

                    <th className="px-4 py-2">Email</th>
                    <th className="px-4 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody className="">
                  {teachers?.map((teacher, idx) => (
                    <tr className="justify-center item-center" key={idx}>
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">
                        {teacher.teacherCode}
                      </td>
                      <td className="px-4 py-2 border">{teacher.lastname}</td>
                      <td className="px-4 py-2 border">{teacher.firstname}</td>

                      <td className="px-4 py-2 border">{teacher.email}</td>

                      <td className="flex items-center justify-center px-3 py-2 border">
                        <Link to={`/admin/teacher/${teacher._id}`}>
                          {/* /admin/teacher/:teacherCode */}
                          <button className="px-2 py-1 mr-5 font-bold text-white rounded bg-primary hover:bg-green-600 focus:outline-none focus:shadow-outline">
                            View
                          </button>
                        </Link>
                        <button className="px-2 py-1 mr-5 font-bold text-white rounded bg-primary hover:bg-green-600 focus:outline-none focus:shadow-outline">
                          Edit
                        </button>
                        <button className="px-2 py-1 font-bold text-white rounded bg-primary hover:bg-green-600 focus:outline-none focus:shadow-outline">
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
    </div>
  );
};

export default Body;
