import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCourseUnit } from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import { SET_ERRORS } from "../../../redux/actionTypes";
import { Link } from "react-router-dom";
import { format } from "date-fns";
const modalStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "0",
  },
};

// http://localhost:9090/api/admin/dsLopTc/lop/CNTT01
const Body = () => {
  const dispatch = useDispatch();
  const [unit, setUnit] = useState("");
  const [error, setError] = useState({});
  const units = useSelector((state) => state.admin.allUnit);
  console.log("units", units);
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    const UnitObj = units.find((dp) => dp.tenLop === unit);
    const UnitId = UnitObj.maLop;
    dispatch(getCourseUnit(UnitId));
  };

  const courses = useSelector((state) => state.admin.courses.retObj);
  console.log("store", store);

  useEffect(() => {
    if (courses?.length !== 0) {
      setLoading(false);
    }
  }, [courses]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  // BEGIN EDIT
  const [value, setValue] = useState({
    id: "",
    maLopTc: "",
    nienKhoa: "",
    ky: "",
    timeBd: "",
    timeKt: "",
    soLuong: "",
    soLuongCon: "",
    maMh: "",
    maGv: "",
    maLop: "",
  });
  const [selectedTeacher, setSelectedTeacher] = useState("");
  console.log("selectedTeacher", selectedTeacher);
  console.log("value", value);
  const [modalMode, setModalMode] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  console.log("isModalOpen", isModalOpen);
  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };
  const handleEditClick = (teacher) => {
    setSelectedTeacher(teacher);
    setIsModalOpen(true);
    setModalMode("edit");
    setValue({
      id: teacher.id,
      maGv: teacher.maGv,
      ho: "",
      ten: "",
      phai: "",
      ngaySinh: "",
      // noiSinh: "",
      // diaChi: "",
      trangThai: teacher.trangThai,
      sdt: "",
      email: teacher.email,
      maKhoa: "",
      hinhAnh: "",
    });
  };
  const handleFormSubmit = (e) => {
    e.preventDefault();
    const updatedValue = {};

    if (value.ho !== "") {
      updatedValue.ho = value.ho;
    } else {
      updatedValue.ho = selectedTeacher.ho;
    }
    if (value.ten !== "") {
      updatedValue.ten = value.ten;
    } else {
      updatedValue.ten = selectedTeacher.ten;
    }
    if (value.phai !== "") {
      updatedValue.phai = value.phai;
    } else {
      updatedValue.phai = selectedTeacher.phai;
    }
    if (value.ngaySinh !== "") {
      updatedValue.ngaySinh = value.ngaySinh;
    } else {
      updatedValue.ngaySinh = selectedTeacher.ngaySinh;
    }
    if (value.sdt !== "") {
      updatedValue.sdt = value.sdt;
    } else {
      updatedValue.sdt = selectedTeacher.sdt;
    }
    if (value.maKhoa !== "") {
      updatedValue.maKhoa = value.maKhoa;
    } else {
      updatedValue.maKhoa = selectedTeacher.maKhoa;
    }
    if (value.hinhAnh !== "") {
      updatedValue.hinhAnh = value.hinhAnh;
    } else {
      updatedValue.hinhAnh = selectedTeacher.hinhAnh;
    }

    dispatch(updateTeacher({ ...selectedTeacher, ...updatedValue }));
    dispatch({ type: UPDATE_TEACHER, payload: false });
    closeModal();
  };

  useEffect(() => {
    dispatch(getTeacherDepartment(selectedTeacher.maKhoa));
  }, [dispatch, store.errors, store.admin.updatedTeacher]);

  //END EDIT

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Danh sách Lớp Tín chỉ</p>

      <div className="flex mt-4">
        <Link to="/admin/addcourse" className="btn btn-primary">
          <button
            className="items-center gap-[9px] mr-4 w-[88px] h-[53px] hover:bg-[#04605E] block py-2 font-bold text-white rounded-lg px-4 
           bg-[#157572] focus:outline-none focus:shadow-outline "
          >
            Thêm
          </button>
        </Link>
        <button
          // onClick={dltSubject}
          className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
        >
          Xóa
        </button>
      </div>

      {/* ô search */}
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Lớp</label>

          <div className="flex">
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={unit}
              onChange={(e) => setUnit(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {units?.map((ut, idx) => (
                <MenuItem key={idx} value={ut.tenLop}>
                  {ut.tenLop}
                </MenuItem>
              ))}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>

      <div className="w-full">
        <div className="col-span-3">
          <div className={classes.loadingAndError}>
            {loading && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#111111"
                messageColor="blue"
              />
            )}
            {/* chỗ này tạm thời để vậy đã */}
            {courses?.length === 0 && (
              <p className="text-2xl font-bold text-red-500">
                Lớp chưa có Lớp tín chỉ
              </p>
            )}
          </div>

          {search &&
            !loading &&
            Object.keys(error).length === 0 &&
            courses?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">Chọn</th>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">Mã Lớp Tín Chỉ</th>
                    <th className="px-4 py-2">Niên Khóa</th>
                    <th className="px-4 py-2">Kỳ</th>

                    <th className="px-4 py-2">Thời Gian Bắt Đầu</th>
                    <th className="px-4 py-2">Thời Gian Kết Thúc</th>
                    <th className="px-4 py-2">Số lượng</th>
                    <th className="px-4 py-2">Số lượng còn</th>

                    <th className="px-4 py-2">Mã Môn Học</th>
                    <th className="px-4 py-2">Mã Giảng Viên</th>
                    <th className="px-4 py-2">Hành Động</th>
                  </tr>
                </thead>
                <tbody className="">
                  {courses?.map((course, idx) => (
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border ">
                        <input
                          // onChange={handleInputChange}
                          // value={dep.id}
                          type="checkbox"
                        />
                      </td>
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">{course.maLopTc}</td>
                      <td className="px-4 py-2 border">{course.nienKhoa}</td>
                      <td className="px-4 py-2 border">{course.ky}</td>

                      <td className="px-4 py-2 border">
                        {format(new Date(course.timeBd), "dd/MM/yyyy")}
                      </td>

                      <td className="px-4 py-2 border">
                        {" "}
                        {format(new Date(course.timeKt), "dd/MM/yyyy")}
                      </td>
                      <td className="px-4 py-2 border">{course.soLuong}</td>
                      <td className="px-4 py-2 border">{course.soLuongCon}</td>

                      <td className="px-4 py-2 border">{course.maMh}</td>
                      <td className="px-4 py-2 border">{course.maGv}</td>

                      <td className="flex flex-col items-center justify-center flex-1 px-4 py-2 mr-0 border gap-y-1">
                        <button className="px-3 py-1  font-bold text-white rounded hover:bg-[#04605E] bg-[#157572]  focus:outline-none focus:shadow-outline">
                          Xem
                        </button>
                        <button
                          className=" w-full h-full px-3 py-1 font-bold text-white rounded  hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                          onClick={() => handleEditClick(course)}
                        >
                          Sửa
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
    </div>
  );
};

export default Body;
