import React, { useEffect, useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import { useDispatch, useSelector } from "react-redux";
import Spinner from "../../../utils/Spinner";
import { ADD_SUBJECT, SET_ERRORS } from "../../../redux/actionTypes";
import * as classes from "../../../utils/styles";
import { Link } from "react-router-dom";
import { addSubject } from "../../../redux/actions/adminActions";
const Body = () => {
  const dispatch = useDispatch();
  const store = useSelector((state) => state);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState({});
  const [value, setValue] = useState({
    maMh: "",
    tenMh: "",
    soTc: "",
  });

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setValue({ ...value, tenMh: "" });
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setError({});
    setLoading(true);
    dispatch(addSubject(value));
  };

  useEffect(() => {
    if (store.errors || store.admin.subjectAdded) {
      setLoading(false);
      if (store.admin.subjectAdded) {
        setValue({
          maMh: "",
          tenMh: "",
          soTc: "",
        });
        dispatch({ type: SET_ERRORS, payload: {} });
        dispatch({ type: ADD_SUBJECT, payload: false });
      }
    } else {
      setLoading(true);
    }
  }, [store.errors, store.admin.subjectAdded]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <div className="space-y-5">
        <div className="flex items-center space-x-2 text-gray-400">
          <AddIcon />
          <h1>Add Subject</h1>
        </div>
        <Link to="/admin/allsubject" className="btn btn-[#157572]">
          <button className="block px-4 py-2 mt-5 font-bold text-white rounded bg-[#157572] mr-14 hover:bg-[#04605E] focus:outline-none focus:shadow-outline">
            Quay lại
          </button>
        </Link>
        <div className="flex flex-col bg-white rounded-xl">
          <form className={classes.adminForm0} onSubmit={handleSubmit}>
            {/* 1 */}
            <div className={classes.adminForm1}>
              <div className={classes.adminForm2l}>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Tên Môn Học :</h1>

                  <input
                    placeholder="Tên Môn Học"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.tenMh}
                    onChange={(e) =>
                      setValue({ ...value, tenMh: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Mã Môn Học :</h1>

                  <input
                    placeholder="Mã Môn Học"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.maMh}
                    onChange={(e) =>
                      setValue({ ...value, maMh: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Số Tín Chỉ :</h1>

                  <input
                    placeholder="Số Tín Chỉ"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.soTc}
                    onChange={(e) =>
                      setValue({ ...value, soTc: e.target.value })
                    }
                  />
                </div>
              </div>
            </div>
            {/* 2 submit and clear*/}
            <div className={classes.adminFormButton}>
              <button className={classes.adminFormSubmitButton} type="submit">
                Submit
              </button>
              <button
                onClick={() => {
                  setValue({
                    maMh: "",
                    tenMh: "",
                    soTc: "",
                  });
                  setError({});
                }}
                className={classes.adminFormClearButton}
                type="button"
              >
                Clear
              </button>
            </div>
            {/* 3 loading */}
            <div className={classes.loadingAndError}>
              {loading && (
                <Spinner
                  message="Đang thêm môn học..."
                  height={30}
                  width={150}
                  color="#157572"
                  messageColor="157572"
                />
              )}
              {error.message ? (
                <p className="text-red-500">{error.message}</p>
              ) : null}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Body;
