import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { getAllTKB } from "../../../redux/actions/adminActions";
import * as classes from "../../../utils/styles";

const Body = () => {
  // const query = {
  //   timeInputBegin: "2023-02-20",
  //   timeInputEnd: "2023-04-30",
  // };
  const dispatch = useDispatch();

  // dispatch(getAllTKB(query));
  const [inputType, setInputType] = useState("text");

  const [value, setValue] = useState({
    timeInputBegin: "",
    timeInputEnd: "",
  });
  console.log("value", value);
  const handleFormSubmit = (e) => {
    e.preventDefault();

    dispatch(getAllTKB(value));
  };
  return (
    <div className="items-center justify-center">
      <div className={classes.WrapInputLabel}>
        <h1 className={classes.LabelStyle}>thời gian bắt đầu :</h1>

        <input
          placeholder="Thời gian bắt đầu"
          className={classes.InputStyle}
          type={inputType}
          value={value.timeInputBegin}
          onChange={(e) =>
            setValue({ ...value, timeInputBegin: e.target.value })
          }
          onFocus={() => setInputType("date")}
          onBlur={() => setInputType("text")}
        />
      </div>
      <div className={classes.WrapInputLabel}>
        <h1 className={classes.LabelStyle}>thời gian kết thúc :</h1>

        <input
          placeholder="Thời gian bắt đầu"
          className={classes.InputStyle}
          type={inputType}
          value={value.timeInputBegin}
          onChange={(e) =>
            setValue({ ...value, timeInputBegin: e.target.value })
          }
          onFocus={() => setInputType("date")}
          onBlur={() => setInputType("text")}
        />
      </div>
    </div>
  );
};

export default Body;
