import {
  ADD_COURSE,
  ADD_COURSE_DETAIL,
  ADD_DEPARTMENT,
  ADD_STUDENT,
  ADD_SUBJECT,
  ADD_TEACHER,
  ADD_UNIT,
  DELETE_DEPARTMENT,
  GET_ALL_COURSE,
  GET_ALL_COURSE_DETAIL,
  GET_ALL_DEPARTMENT,
  GET_ALL_STUDENT,
  GET_ALL_SUBJECT,
  GET_ALL_TEACHER,
  GET_ALL_TKB,
  GET_ALL_UNIT,
  GET_COURSE_UNIT,
  GET_SCORE_COURSE,
  GET_STUDENT_BY_ID,
  GET_STUDENT_UNIT,
  GET_TEACHER_DEPARTMENT,
  GET_UNIT_DEPARTMENT,
  LOGIN,
  LOGOUT,
  UPDATE_COURSE,
  UPDATE_DEPARTMENT,
  UPDATE_STUDENT,
  UPDATE_SUBJECT,
  UPDATE_TEACHER,
  UPDATE_UNIT,
} from "../actionTypes";

const initialState = {
  authData: null,
  // edit
  updatedDepartment: false,
  updatedUnit: false,
  updatedSubject: false,
  updatedStudent: false,
  updatedTeacher: false,
  updatedCourse: false,
  // add
  departmentAdded: false,
  teacherAdded: false,
  studentAdded: false,
  subjectAdded: false,
  coursedetailAdded: false,
  //getll
  allTeacher: [],
  allSubject: [],
  allStudent: [],
  allDepartment: [],
  allCourseDetail: [],
  //getbyidby~
  students: [],
  teachers: [],
  subjects: [],
  admins: [],
  units: [],
  courses: [],
  scores: [],
  //delete
  adminDeleted: false,
  departmentDeleted: false,
  teacherDeleted: false,
  studentDeleted: false,
  subjectDeleted: false,
  allTKB: false,
};

const adminReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN:
      localStorage.setItem("user", JSON.stringify({ ...action?.data }));
      return { ...state, authData: action?.data };
    case LOGOUT:
      localStorage.clear();
      return { ...state, authData: null };
    case GET_ALL_DEPARTMENT:
      return {
        ...state,
        allDepartment: action.payload,
      };
    case ADD_DEPARTMENT:
      return {
        ...state,
        departmentAdded: action.payload,
      };
    case DELETE_DEPARTMENT:
      return {
        ...state,
        departmentDeleted: action.payload,
      };
    case GET_TEACHER_DEPARTMENT:
      return {
        ...state,
        teachers: action.payload,
      };
    case GET_ALL_TEACHER:
      return {
        ...state,
        allTeacher: action.payload,
      };
    case ADD_TEACHER:
      return {
        ...state,
        teacherAdded: action.payload,
      };

    case ADD_UNIT:
      return {
        ...state,
        united: action.payload,
      };
    case GET_ALL_UNIT:
      return {
        ...state,
        allUnit: action.payload,
      };
    case GET_STUDENT_UNIT:
      return {
        ...state,
        students: action.payload,
      };

    case GET_ALL_SUBJECT:
      return {
        ...state,
        allSubject: action.payload,
      };
    case ADD_STUDENT:
      return {
        ...state,
        studentAdded: action.payload,
      };
    case ADD_SUBJECT:
      return {
        ...state,
        subjectAdded: action.payload,
      };
    case ADD_COURSE:
      return {
        ...state,
        courseAdded: action.payload,
      };
    case UPDATE_DEPARTMENT:
      return {
        ...state,
        updatedDepartment: action.payload,
      };
    case GET_UNIT_DEPARTMENT:
      return {
        ...state,
        units: action.payload,
      };
    case GET_ALL_STUDENT:
      return {
        ...state,
        allStudent: action.payload,
      };
    case GET_COURSE_UNIT:
      return {
        ...state,
        courses: action.payload,
      };
    case GET_SCORE_COURSE:
      return {
        ...state,
        scores: action.payload,
      };
    case GET_ALL_COURSE:
      return {
        ...state,
        allCourse: action.payload,
      };
    case UPDATE_UNIT:
      return {
        ...state,
        updatedUnit: action.payload,
      };
    case UPDATE_SUBJECT:
      return {
        ...state,
        updatedSubject: action.payload,
      };
    case UPDATE_STUDENT:
      return {
        ...state,
        updatedStudent: action.payload,
      };

    case GET_STUDENT_BY_ID:
      return {
        ...state,
        students: action.payload,
      };
    case UPDATE_TEACHER:
      return {
        ...state,
        updatedTeacher: action.payload,
      };
    case GET_ALL_TKB:
      return {
        ...state,
        allTKB: action.payload,
      };
    case ADD_COURSE_DETAIL:
      return {
        ...state,
        coursedetailAdded: action.payload,
      };
    case UPDATE_COURSE:
      return {
        ...state,
        updatedCourse: action.payload,
      };
    case GET_ALL_COURSE_DETAIL:
      return {
        ...state,
        allCourseDetail: action.payload,
      };
    default:
      return state;
  }
};

export default adminReducer;
