import React, { useEffect, useState } from "react";
import EngineeringIcon from "@mui/icons-material/Engineering";
import { useDispatch, useSelector } from "react-redux";
import { getAllTeacher, getTeacher } from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import { SET_ERRORS } from "../../../redux/actionTypes";
import { Link } from "react-router-dom";
const Body = () => {
  const dispatch = useDispatch();
  const [department, setDepartment] = useState("");
  const [error, setError] = useState({});
  const departments = useSelector((state) => state.admin.allDepartment);
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    const departmentObj = departments.find(
      (dp) => dp.departmentName === department
    );
    const departmentId = departmentObj._id;
    console.log("departmentId", departmentId);
    dispatch(getTeacher(departmentId));
  };

  const teachers = useSelector((state) => state.admin.teachers.data);

  useEffect(() => {
    if (teachers?.length !== 0) {
      setLoading(false);
    }
  }, [teachers]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 flex flex-col ml-14">
      <div className="flex justify-between">
        <p className="block text-black ">Teachers List</p>
        <Link to="/admin/adddepartment" className="btn btn-primary">
          <button className="block px-4 py-2 mr-24 font-bold text-white rounded bg-primary hover:bg-green-600 focus:outline-none focus:shadow-outline">
            Add Teacher
          </button>
        </Link>
      </div>

      <div className="flex my-8 ">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Department</label>
          <div className="flex">
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={department}
              onChange={(e) => setDepartment(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {departments?.map((dp, idx) => (
                <MenuItem key={idx} value={dp.departmentName}>
                  {dp.departmentName}
                </MenuItem>
              ))}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>

      <div className="w-11/12">
        <div className="col-span-3 mr-6">
          <div className={classes.loadingAndError}>
            {loading && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#111111"
                messageColor="blue"
              />
            )}
            {(error.noTeacherError || error.backendError) && (
              <p className="text-2xl font-bold text-red-500">
                {error.noTeacherError || error.backendError}
              </p>
            )}
          </div>

          {search &&
            !loading &&
            Object.keys(error).length === 0 &&
            teachers?.length !== 0 && (
              <table className="w-full table-auto">
                <thead>
                  <tr>
                    <th className="px-4 py-2">Sr no.</th>
                    <th className="px-4 py-2">Teacher Code</th>
                    <th className="px-4 py-2">Last name</th>
                    <th className="px-4 py-2">First name</th>
                    <th className="px-4 py-2">dob</th>
                    <th className="px-4 py-2">contactNumber</th>
                    <th className="px-4 py-2">email</th>
                    <th className="px-4 py-2">avatar</th>
                  </tr>
                </thead>
                <tbody className="">
                  {teachers?.map((teacher, idx) => (
                    <tr className="justify-center item-center" key={idx}>
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">
                        {teacher.teacherCode}
                      </td>
                      <td className="px-4 py-2 border">{teacher.lastname}</td>
                      <td className="px-4 py-2 border">{teacher.firstname}</td>
                      <td className="px-4 py-2 border">{teacher.dob}</td>
                      <td className="px-4 py-2 border">{teacher.gender}</td>
                      <td className="px-4 py-2 border">
                        {teacher.contactNumber}
                      </td>
                      <td className="px-4 py-2 border">{teacher.email}</td>
                      <td className="px-4 py-2 border">{teacher.avatar}</td>

                      <td className="items-center justify-center px-4 py-2 border">
                        <button className="px-2 py-1 mr-5 font-bold text-white rounded bg-primary hover:bg-blue-700 focus:outline-none focus:shadow-outline">
                          Edit
                        </button>
                        <button className="px-2 py-1 font-bold text-white rounded bg-primary hover:bg-red-700 focus:outline-none focus:shadow-outline">
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
    </div>
  );
};

export default Body;
