import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { getAllTKB } from "../../../redux/actions/adminActions";

const Body = () => {
  // const query = {
  //   timeInputBegin: "2023-02-20",
  //   timeInputEnd: "2023-04-30",
  // };
  // const dispatch = useDispatch();

  // dispatch(getAllTKB(query));

  const [value, setValue] = useState({
    timeInputBegin: "",
    timeInputEnd: "",
  });
  const [selectedCourse, setSelectedCourse] = useState("");
  console.log("selectedCourse", selectedCourse);
  console.log("value", value);
  const [isModalOpen, setIsModalOpen] = useState(false);
  console.log("isModalOpen", isModalOpen);
  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };
  const handleEditClick = (course) => {
    setSelectedCourse(course);
    setIsModalOpen(true);
    setModalMode("edit");
    setValue({
      id: course.id,
      maLopTc: course.maLopTc,
      nienKhoa: "",
      ky: "",
      timeBd: "",
      timeKt: "",
      soLuong: "",
      soLuongCon: course.soLuongCon,
      maMh: "",
      maGv: "",
      maLop: "",
    });
  };
  const handleFormSubmit = (e) => {
    e.preventDefault();
    const updatedValue = {};

    if (value.nienKhoa !== "") {
      updatedValue.nienKhoa = value.nienKhoa;
    } else {
      updatedValue.nienKhoa = selectedCourse.nienKhoa;
    }
    if (value.ky !== "") {
      updatedValue.ky = value.ky;
    } else {
      updatedValue.ky = selectedCourse.ky;
    }
    if (value.timeBd !== "") {
      updatedValue.timeBd = value.timeBd;
    } else {
      updatedValue.timeBd = selectedCourse.timeBd;
    }
    if (value.timeKt !== "") {
      updatedValue.timeKt = value.timeKt;
    } else {
      updatedValue.timeKt = selectedCourse.timeKt;
    }
    if (value.soLuong !== "") {
      updatedValue.soLuong = value.soLuong;
    } else {
      updatedValue.soLuong = selectedCourse.soLuong;
    }
    if (value.maMh !== "") {
      updatedValue.maMh = value.maMh;
    } else {
      updatedValue.maMh = selectedCourse.maMh;
    }
    if (value.maGv !== "") {
      updatedValue.maGv = value.maGv;
    } else {
      updatedValue.maGv = selectedCourse.maGv;
    }
    if (value.maLop !== "") {
      updatedValue.maLop = value.maLop;
    } else {
      updatedValue.maLop = selectedCourse.maLop;
    }

    dispatch(updateCourse({ ...selectedCourse, ...updatedValue }));
    dispatch({ type: UPDATE_COURSE, payload: false });
    closeModal();
  };
  return <div className="items-center justify-center"></div>;
};

export default Body;
