import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import { useDispatch, useSelector } from "react-redux";
import { adminSignIn } from "../../../redux/actions/adminActions";
import VisibilityIcon from "@mui/icons-material/Visibility";
import VisibilityOffIcon from "@mui/icons-material/VisibilityOff";
import Spinner from "../../../utils/Spinner";

const AdminLogin = () => {
  // const [translate, setTranslate] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const store = useSelector((state) => state);
  const [error, setError] = useState({});
  // useEffect(() => {
  //   setTimeout(() => {
  //     setTranslate(true);
  //   }, 1000);
  // }, []);

  useEffect(() => {
    if (store.errors) {
      setError(store.errors);
    }
  }, [store.errors]);

  const login = (e) => {
    e.preventDefault();
    setLoading(true);
    dispatch(adminSignIn({ username: username, password: password }, navigate));
  };

  useEffect(() => {
    if (store.errors) {
      setLoading(false);
      setUsername("");
      setPassword("");
    }
  }, [store.errors]);
  return (
    <div className="flex grid grid-cols-2 ">
      {/* colum 1 */}
      <div>
        <img
          class="object-cover w-full h-full"
          src="https://www.genesiscareer.edu/wp-content/uploads/2021/06/Untitled-design-1-1280x720.png"
          alt=""
        />
      </div>
      {/* column 2 */}
      <div className="bg-white h-screen flex items-center justify-center">
        <div>
          {/* form */}
          <form
            onSubmit={login}
            className={`${
              loading ? "h-[27rem]" : "h-96"
            } w-96 bg-white flex flex-col items-center justify-center duration-1000  space-y-6 rounded-3xl shadow-2xl`}
            // translate ? "-translate-x-[0rem]" : ""
            // }  duration-1000  space-y-6 rounded-3xl shadow-2xl`}
          >
            <h1 className="text-gray-800 text-3xl font-semibold">Admin</h1>

            <div className="space-y-1">
              <label
                for="email"
                className="block text-sm font-semibold text-gray-800"
              >
                Username
              </label>
              <div className="bg-white rounded-lg w-[16rem] flex  items-center">
                <input
                  onChange={(e) => setUsername(e.target.value)}
                  value={username}
                  type="email"
                  required
                  className="bg-white text-black rounded-lg placeholder:text-sm block w-full px-4 py-2 mt-2   border focus:border-green-400 focus:ring-green-300 focus:outline-none focus:ring focus:ring-opacity-40"
                  placeholder="Enter your username"
                />
              </div>
            </div>

            <div className="space-y-1">
              <label
                for="password"
                className="block text-sm font-semibold text-gray-800"
              >
                Password
              </label>

              <div className="bg-white rounded-lg w-[16rem] flex  items-center">
                <input
                  onChange={(e) => setPassword(e.target.value)}
                  value={password}
                  required
                  type={showPassword ? "text" : "password"}
                  className="bg-white text-black rounded-lg placeholder:text-sm block w-full px-4 py-2 mt-2 border focus:border-green-400 focus:ring-green-300 focus:outline-none focus:ring focus:ring-opacity-40 relative"
                  placeholder="Enter your password"
                />
                {showPassword ? (
                  <VisibilityIcon
                    onClick={() => setShowPassword(!showPassword)}
                    className="cursor-pointer absolute right-[264px] mt-2"
                  />
                ) : (
                  <VisibilityOffIcon
                    onClick={() => setShowPassword(!showPassword)}
                    className="cursor-pointer absolute right-[72px] mt-2"
                  />
                )}
              </div>
            </div>

            <button
              type="submit"
              className="w-32 hover:scale-105  duration-150 rounded-lg flex items-center justify-center text-white text-base py-1 bg-[#04bd7d]"
            >
              Login
            </button>
            {loading && (
              <Spinner
                message="Logging In"
                height={30}
                width={150}
                color="#ffffff"
                messageColor="#fff"
              />
            )}
            {(error.usernameError || error.passwordError) && (
              <p className="text-red-500">
                {error.usernameError || error.passwordError}
              </p>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
