import React, { useEffect, useState } from "react";
import ReactModal from "react-modal";
import { useSelector, useDispatch } from "react-redux";
import {
  getAllDepartment,
  updateDepartment,
} from "../../../redux/actions/adminActions";
import { Link } from "react-router-dom";
import { deleteDepartment } from "../../../redux/actions/adminActions";
import * as classes from "../../../utils/styles";
import { UPDATE_DEPARTMENT } from "../../../redux/actionTypes";
const modalStyles = {
  content: {
    top: "45%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "0",
  },
};
const Body = () => {
  const store = useSelector((state) => state);
  const departments = useSelector((state) => state.admin.allDepartment);
  const dispatch = useDispatch();
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [checkedValue, setCheckedValue] = useState([]);
  const [value, setValue] = useState({
    maKhoa: "",
    tenKhoa: "",
    sdt: "",
    email: "",
    id: "",
  });
  const [isModalOpen, setIsModalOpen] = useState(false);
  useEffect(() => {
    dispatch(getAllDepartment());
  }, [dispatch]);

  // bấm vào sửa thì chọn khoa, mở modal, set sẵn value 3 cái mặc định đi
  const handleEditClick = (dep) => {
    setSelectedDepartment(dep);
    setIsModalOpen(true);
    setValue({
      sdt: "",
      tenKhoa: "",
      maKhoa: dep.maKhoa,
      email: dep.email,
      id: dep.id,
    });
  };
  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };
  //modal
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!value.tenKhoa && !value.sdt) {
      alert("Enter at least one value");
    } else {
      const updatedValue = {};
      if (value.tenKhoa !== "") {
        updatedValue.tenKhoa = value.tenKhoa;
      } else {
        updatedValue.tenKhoa = selectedDepartment.tenKhoa;
      }
      if (value.sdt !== "") {
        updatedValue.sdt = value.sdt;
      } else {
        updatedValue.sdt = selectedDepartment.sdt;
      }
      dispatch(updateDepartment({ ...selectedDepartment, ...updatedValue }));
      dispatch({ type: UPDATE_DEPARTMENT, payload: false });
    }
    closeModal();
  };

  useEffect(() => {
    dispatch(getAllDepartment());
  }, [dispatch, store.errors, store.admin.updatedDepartment]);

  // check xóa
  const handleInputChange = (e) => {
    const tempCheck = checkedValue;
    let index;
    if (e.target.checked) {
      tempCheck.push(e.target.value);
    } else {
      index = tempCheck.indexOf(e.target.value);
      tempCheck.splice(index, 1);
    }
    setCheckedValue(tempCheck);
  };
  // hàm xóa
  const dltSubject = (e) => {
    // console.log("checkedValue", checkedValue);
    dispatch(deleteDepartment(checkedValue));
  };
  // câp nhật lại
  console.log("store", store);
  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Danh sách khoa</p>

      <div className="flex mt-4">
        <Link to="/admin/adddepartment" className="btn btn-primary">
          <button
            className="items-center gap-[9px] mr-4 w-[88px] h-[53px] hover:bg-[#04605E] block py-2 font-bold text-white rounded-lg px-4 
           bg-[#157572] focus:outline-none focus:shadow-outline "
          >
            Thêm
          </button>
        </Link>
        <button
          onClick={dltSubject}
          className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
        >
          Xóa
        </button>
      </div>

      <div className="items-center my-8 mt-6 mb-2 rounded-lg">
        <div className="relative w-[524px] h-[38px] text-gray-600 border border-[#157572] rounded-lg ">
          <input
            className="w-full h-full px-5 pr-10 text-sm rounded-lg focus:outline-none"
            type="search"
            name="search"
            placeholder="Department search"
          />
          <button type="submit" className="absolute top-0 right-0 mt-3 mr-4">
            <svg
              className="w-4 h-4 fill-current"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
            >
              <path
                fillRule="evenodd"
                d="M13.822 14.509a8.5 8.5 0 111.768-1.768l3.866 3.865a1 1 0 01-1.414 1.414l-3.866-3.866zM8.5 15.5a7 7 0 100-14 7 7 0 000 14z"
                clipRule="evenodd"
              />
            </svg>
          </button>
        </div>
      </div>

      <div className="w-full">
        {departments?.length !== 0 && (
          <table className="w-full table-auto ">
            <thead className="bg-[#E1EEEE] items-center">
              <tr>
                <th className="px-4 py-2">Chọn</th>
                <th className="px-4 py-2">STT</th>
                <th className="px-4 py-2">Mã Khoa</th>
                <th className="px-4 py-2">Tên Khoa</th>
                <th className="px-4 py-2">SĐT</th>
                <th className="px-4 py-2">Email</th>
                <th className="px-4 py-2">Hành động</th>
              </tr>
            </thead>
            <tbody className="">
              {departments?.map((dep, idx) => (
                <tr
                  className="justify-center item-center hover:bg-[#EEF5F5]"
                  key={idx}
                >
                  <td className="px-4 py-2 border">
                    <input
                      onChange={handleInputChange}
                      value={dep.id}
                      type="checkbox"
                    />
                  </td>
                  <td className="px-4 py-2 border">{idx + 1}</td>
                  <td className="px-4 py-2 border">{dep.maKhoa}</td>
                  <td className="px-4 py-2 border">{dep.tenKhoa}</td>
                  <td className="px-4 py-2 border">{dep.sdt}</td>
                  <td className="px-4 py-2 border">{dep.email}</td>
                  <td
                    className="items-center justify-center px-4 py-2 mr-0 border"
                    style={{ display: "flex", justifyContent: "center" }}
                  >
                    <button
                      className="px-3.5 py-1 font-bold text-white rounded hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                      onClick={() => handleEditClick(dep)}
                    >
                      Sửa
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {selectedDepartment ? (
        <ReactModal
          isOpen={isModalOpen}
          onRequestClose={openModal}
          style={modalStyles}
          ariaHideApp={false}
        >
          <div className="flex flex-col bg-white rounded-xl ">
            <form
              className="w-full min-h-[300px] py-10 px-7 text-center bg-[#fff] border rounded-md  shadow-md mx-auto"
              onSubmit={handleFormSubmit}
            >
              <div className={classes.FormItem}>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Tên Khoa :</h1>
                  <input
                    placeholder={selectedDepartment?.tenKhoa}
                    disabled
                    className={classes.InputStyle}
                    type="text"
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Ten Khoa :</h1>
                  <input
                    placeholder={selectedDepartment?.tenKhoa}
                    className={classes.InputStyle}
                    type="text"
                    value={value.tenKhoa}
                    onChange={(e) =>
                      setValue({
                        ...value,
                        tenKhoa: e.target.value,
                      })
                    }
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Số điện thoại :</h1>
                  <input
                    placeholder={selectedDepartment?.sdt}
                    className={classes.InputStyle}
                    type="text"
                    value={value.sdt}
                    onChange={(e) =>
                      setValue({
                        ...value,
                        sdt: e.target.value,
                      })
                    }
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Email :</h1>
                  <input
                    placeholder={selectedDepartment?.email}
                    disabled
                    className={classes.InputStyle}
                    type="text"
                  />
                </div>
              </div>

              <div className="flex items-center justify-center mt-10 space-x-6">
                <button className={classes.adminFormSubmitButton} type="submit">
                  Submit
                </button>
                <Link to="/admin/getdepartmentall" className="btn btn-primary">
                  <button
                    className={classes.adminFormClearButton}
                    type="button"
                    onClick={closeModal}
                  >
                    Cancel
                  </button>
                </Link>
              </div>
            </form>
          </div>
        </ReactModal>
      ) : null}
    </div>
  );
};
export default Body;
