import React, { useEffect, useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import { useDispatch, useSelector } from "react-redux";
import FileBase from "react-file-base64";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import Spinner from "../../../utils/Spinner";
import {
  ADD_COURSE,
  ADD_STUDENT,
  SET_ERRORS,
} from "../../../redux/actionTypes";
import * as classes from "../../../utils/styles";
import { Link } from "react-router-dom";
import { addCourse } from "../../../redux/actions/adminActions";
const Body = () => {
  const dispatch = useDispatch();
  const store = useSelector((state) => state);
  console.log("store", store);
  const units = useSelector((state) => state.admin.allUnit);
  const teachers = useSelector((state) => state.admin.allTeacher);
  const subjects = useSelector((state) => state.admin.allSubject);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState({});
  const [value, setValue] = useState({
    maLopTc: "",
    nienKhoa: "",
    ky: "",
    timeBd: "",
    timeKt: "",
    maMh: "",
    maGv: "",
    maLop: "",
  });

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setValue({ ...value, maLopTc: "" });
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setError({});
    setLoading(true);
    console.log("value", value);

    dispatch(addCourse(value));
  };

  useEffect(() => {
    if (store.errors || store.admin.courseAdded) {
      setLoading(false);
      if (store.admin.courseAdded) {
        setValue({
          maLopTc: "",
          nienKhoa: "",
          ky: "",
          timeBd: "",
          timeKt: "",
          maMh: "",
          maGv: "",
          maLop: "",
        });
        dispatch({ type: SET_ERRORS, payload: {} });
        dispatch({ type: ADD_COURSE, payload: false });
      }
    } else {
      setLoading(true);
    }
  }, [store.errors, store.admin.courseAdded]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 ">
      <div className="space-y-5">
        <div className="flex items-center space-x-2 text-gray-400">
          <AddIcon />
          <h1>Add Course</h1>
        </div>
        <Link to="/admin/allcourse" className="btn btn-primary">
          <button className="block px-4 py-2 mt-5 ml-5 font-bold text-white rounded bg-primary mr-14 hover:bg-green-600 focus:outline-none focus:shadow-outline">
            Quay lại
          </button>
        </Link>
        <div className="flex flex-col h-full pb-6 ml-3 mr-5 bg-white rounded-xl">
          <form className={classes.adminForm0} onSubmit={handleSubmit}>
            <div className={classes.adminForm1}>
              <div className={classes.adminForm2l}>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Mã Lớp Tín Chỉ :</h1>

                  <input
                    placeholder="Mã Lớp Tín Chỉ"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.maLopTc}
                    onChange={(e) =>
                      setValue({ ...value, maLopTc: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Niên Khóa :</h1>

                  <input
                    placeholder="Niên Khóa"
                    required
                    className={classes.adminInput}
                    type="number"
                    value={value.nienKhoa}
                    onChange={(e) =>
                      setValue({ ...value, nienKhoa: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Kỳ :</h1>

                  <input
                    placeholder="First Name"
                    required
                    className={classes.adminInput}
                    type="number"
                    value={value.ky}
                    onChange={(e) => setValue({ ...value, ky: e.target.value })}
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Thời gian bắt đầu :</h1>

                  <input
                    placeholder="Thời gian bắt đầu"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.timeBd}
                    onChange={(e) =>
                      setValue({ ...value, timeBd: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Thời Gian Kết Thúc :</h1>

                  <input
                    placeholder="Thời gian kết thúc"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.timeKt}
                    onChange={(e) =>
                      setValue({ ...value, timeKt: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Lớp :</h1>
                  <Select
                    required
                    displayEmpty
                    sx={{ height: 36 }}
                    inputProps={{ "aria-label": "Without label" }}
                    value={value.maLop}
                    onChange={(e) =>
                      setValue({ ...value, maLop: e.target.value })
                    }
                  >
                    <MenuItem value="">None</MenuItem>
                    {units?.map((dp, idx) => (
                      <MenuItem key={idx} value={dp.maLop}>
                        {dp.tenLop}
                      </MenuItem>
                    ))}
                  </Select>
                </div>
              </div>
              <div className={classes.adminForm2r}>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Giảng Viên :</h1>
                  <Select
                    required
                    displayEmpty
                    sx={{ height: 36 }}
                    inputProps={{ "aria-label": "Without label" }}
                    value={value.maGv}
                    onChange={(e) =>
                      setValue({ ...value, maGv: e.target.value })
                    }
                  >
                    <MenuItem value="">None</MenuItem>
                    {teachers?.map((teacher, idx) => (
                      <MenuItem key={idx} value={teacher.maGv}>
                        {teacher.ho} {teacher.ten}
                      </MenuItem>
                    ))}
                  </Select>
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Môn Học :</h1>
                  <Select
                    required
                    displayEmpty
                    sx={{ height: 36 }}
                    inputProps={{ "aria-label": "Without label" }}
                    value={value.maMh}
                    onChange={(e) =>
                      setValue({ ...value, maMh: e.target.value })
                    }
                  >
                    <MenuItem value="">None</MenuItem>
                    {subjects?.map((subject, idx) => (
                      <MenuItem key={idx} value={subject.maMh}>
                        {subject.tenMh}
                      </MenuItem>
                    ))}
                  </Select>
                </div>
              </div>
            </div>
            <div className={classes.adminFormButton}>
              <button className={classes.adminFormSubmitButton} type="submit">
                Submit
              </button>
              <button
                onClick={() => {
                  setValue({
                    maLopTc: "",
                    nienKhoa: "",
                    ky: "",
                    timeBd: "",
                    timeKt: "",
                    maMh: "",
                    maGv: "",
                    maLop: "",
                  });
                  setError({});
                }}
                className={classes.adminFormClearButton}
                type="button"
              >
                Clear
              </button>
            </div>
            <div className={classes.loadingAndError}>
              {loading && (
                <Spinner
                  message="Adding Teacher"
                  height={30}
                  width={150}
                  color="#111111"
                  messageColor="blue"
                />
              )}

              {error.message ? (
                <p className="text-red-500">{error.message}</p>
              ) : null}
            </div>
          </form>
        </div>
      </div>
