import React, { useEffect, useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import { useDispatch, useSelector } from "react-redux";
import FileBase from "react-file-base64";
import { addTeacher } from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import Spinner from "../../../utils/Spinner";
import { ADD_TEACHER, SET_ERRORS } from "../../../redux/actionTypes";
import * as classes from "../../../utils/styles";
import { Link } from "react-router-dom";
const Body = () => {
  const dispatch = useDispatch();
  const store = useSelector((state) => state);
  const departments = useSelector((state) => state.admin.allDepartment);
  console.log("departments", departments);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState({});
  const [value, setValue] = useState({
    maGv: "",
    ho: "",
    ten: "",
    phai: "",
    ngaysinh: "",
    noisinh: "",
    diachi: "",
    sdt: "",
    email: "",
    maKhoa: "",
    hinhAnh: "",
  });

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setValue({ ...value, email: "" });
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setError({});
    setLoading(true);
    dispatch(addTeacher(value));
  };

  useEffect(() => {
    if (store.errors || store.admin.teacherAdded) {
      setLoading(false);
      if (store.admin.teacherAdded) {
        setValue({
          maGv: "",
          ho: "",
          ten: "",
          phai: "",
          ngaysinh: "",
          noisinh: "",
          diachi: "",
          sdt: "",
          email: "",
          maKhoa: "",
          hinhAnh: "",
        });
        dispatch({ type: SET_ERRORS, payload: {} });
        dispatch({ type: ADD_TEACHER, payload: false });
      }
    } else {
      setLoading(true);
    }
  }, [store.errors, store.admin.teacherAdded]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 ">
      <div className="space-y-5">
        <div className="flex items-center space-x-2 text-gray-400">
          <AddIcon />
          <h1>Add Teacher</h1>
        </div>
        <Link to="/admin/allteacher" className="btn btn-primary">
          <button className="block px-4 py-2 mt-5 ml-5 font-bold text-white rounded bg-primary mr-14 hover:bg-green-600 focus:outline-none focus:shadow-outline">
            Quay lại
          </button>
        </Link>
        <div className="flex flex-col h-full pb-6 ml-3 mr-5 bg-white rounded-xl">
          <form className={classes.adminForm0} onSubmit={handleSubmit}>
            <div className={classes.adminForm1}>
              <div className={classes.adminForm2l}>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Teacher Code :</h1>

                  <input
                    placeholder="Teacher Code"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.maGv}
                    onChange={(e) =>
                      setValue({ ...value, maGv: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Last Name :</h1>

                  <input
                    placeholder="last name"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.ho}
                    onChange={(e) => setValue({ ...value, ho: e.target.value })}
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>First Name :</h1>

                  <input
                    placeholder="First Name"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.ten}
                    onChange={(e) =>
                      setValue({ ...value, ten: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>DOB :</h1>

                  <input
                    placeholder="YYYY-MM-DD"
                    required
                    className={classes.adminInput}
                    type="date"
                    value={value.ngaysinh}
                    onChange={
                      (e) => {
                        const date = new Date(e.target.value);
                        const formattedDate = `${date.getFullYear()}-${(
                          date.getMonth() + 1
                        )
                          .toString()
                          .padStart(2, "0")}-${date
                          .getDate()
                          .toString()
                          .padStart(2, "0")}`;
                        setValue({ ...value, ngaysinh: formattedDate });
                      }
                      // setValue({ ...value, ngaysinh: e.target.value })
                    }
                  />
                </div>

                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Email :</h1>

                  <input
                    placeholder="Email"
                    required
                    className={classes.adminInput}
                    type="email"
                    value={value.email}
                    onChange={(e) =>
                      setValue({ ...value, email: e.target.value })
                    }
                  />
                </div>
              </div>
              <div className={classes.adminForm2r}>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Department :</h1>
                  <Select
                    required
                    displayEmpty
                    sx={{ height: 36 }}
                    inputProps={{ "aria-label": "Without label" }}
                    value={value.maKhoa}
                    onChange={(e) =>
                      setValue({ ...value, maKhoa: e.target.value })
                    }
                  >
                    <MenuItem value="">None</MenuItem>
                    {departments?.map((dp, idx) => (
                      <MenuItem key={idx} value={dp.maKhoa}>
                        {dp.tenKhoa}
                      </MenuItem>
                    ))}
                  </Select>
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Gender :</h1>
                  <Select
                    required
                    displayEmpty
                    sx={{ height: 36 }}
                    inputProps={{ "aria-label": "Without label" }}
                    value={value.phai}
                    onChange={(e) =>
                      setValue({ ...value, phai: e.target.value })
                    }
                  >
                    <MenuItem value="">None</MenuItem>
                    <MenuItem value="Male">Nam</MenuItem>
                    <MenuItem value="Female">Nữ</MenuItem>
                  </Select>
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Nơi Sinh :</h1>

                  <input
                    placeholder="Nơi Sinh"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.noisinh}
                    onChange={(e) =>
                      setValue({ ...value, noisinh: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Địa chỉ :</h1>

                  <input
                    placeholder="Địa Chỉ"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.diachi}
                    onChange={(e) =>
                      setValue({ ...value, diachi: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Contact Number :</h1>

                  <input
                    required
                    placeholder="Contact Number"
                    className={classes.adminInput}
                    type="text"
                    value={value.sdt}
                    onChange={(e) =>
                      setValue({ ...value, sdt: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Avatar :</h1>

                  <FileBase
                    type="file"
                    multiple={false}
                    onDone={({ base64 }) =>
                      setValue({ ...value, hinhAnh: base64 })
                    }
                  />
                </div>
              </div>
            </div>
            <div className={classes.adminFormButton}>
              <button className={classes.adminFormSubmitButton} type="submit">
                Submit
              </button>
              <button
                onClick={() => {
                  setValue({
                    maGv: "",
                    ho: "",
                    ten: "",
                    phai: "",
                    ngaysinh: "",
                    noisinh: "",
                    diachi: "",
                    sdt: "",
                    email: "",
                    maKhoa: "",
                    hinhAnh: "",
                  });
                  setError({});
                }}
                className={classes.adminFormClearButton}
                type="button"
              >
                Clear
              </button>
            </div>
            <div className={classes.loadingAndError}>
              {loading && (
                <Spinner
                  message="Adding Teacher"
                  height={30}
                  width={150}
                  color="#111111"
                  messageColor="blue"
                />
              )}
              {(error.TeacherError || error.backendError) && (
                <p className="text-red-500">
                  {error.TeacherError || error.backendError}
                </p>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Body;
