import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import { useDispatch, useSelector } from "react-redux";
import { signin } from "../../../redux/actions/adminActions";
import VisibilityIcon from "@mui/icons-material/Visibility";
import VisibilityOffIcon from "@mui/icons-material/VisibilityOff";
import Spinner from "../../../utils/Spinner";
import { facultySignIn } from "../../../redux/actions/facultyActions";

const FacultyLogin = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const store = useSelector((state) => state);
  const [error, setError] = useState({});

  useEffect(() => {
    if (store.errors) {
      setError(store.errors);
    }
  }, [store.errors]);

  const login = (e) => {
    e.preventDefault();
    setLoading(true);
    dispatch(
      facultySignIn({ username: username, password: password }, navigate)
    );
  };

  useEffect(() => {
    if (store.errors) {
      setLoading(false);
      setUsername("");
      setPassword("");
    }
  }, [store.errors]);
  return (
    <div className="flex items-center justify-center h-screen bg-lite isolate">
      <form
        onSubmit={login}
        className={`${
          loading ? "h-[27rem]" : "h-96"
        } w-96 bg-white flex flex-col items-center justify-center space-y-6 rounded-2xl shadow-2xl`}
      >
        <h1 className="text-3xl font-semibold text-text1">Teacher</h1>

        <div className="space-y-1">
          <p className="block text-sm font-semibold text-gray-800">Username</p>
          <div className="bg-white rounded-lg w-[16rem] flex  items-center">
            <input
              onChange={(e) => setUsername(e.target.value)}
              value={username}
              type="text"
              required
              className="block w-full px-4 py-2 mt-2 text-black bg-white border rounded-lg placeholder:text-sm focus:border-green-400 focus:ring-green-300 focus:outline-none focus:ring focus:ring-opacity-40"
              placeholder="Username"
            />
          </div>
        </div>
        <div className="space-y-1">
          <p className="block text-sm font-semibold text-gray-800">Password</p>

          <div className="bg-white rounded-lg w-[256px] flex  items-center">
            <input
              onChange={(e) => setPassword(e.target.value)}
              value={password}
              required
              type={showPassword ? "text" : "password"}
              className="relative block w-full px-4 py-2 mt-2 text-black bg-white border rounded-lg outline-none placeholder:text-sm focus:border-green-400 focus:ring-green-300 focus:outline-none focus:ring focus:ring-opacity-40"
              placeholder="Password"
            />
            <div className="absolute ml-[224px] mt-2">
              {showPassword ? (
                <VisibilityIcon
                  onClick={() => setShowPassword(!showPassword)}
                  className="cursor-pointer"
                />
              ) : (
                <VisibilityOffIcon
                  onClick={() => setShowPassword(!showPassword)}
                  className="cursor-pointer"
                />
              )}
            </div>
          </div>
        </div>

        <button
          type="submit"
          className="w-32 hover:scale-105 transition-all duration-150 rounded-lg flex items-center justify-center text-white text-base py-1 bg-primary"
        >
          Login
        </button>
        {loading && (
          <Spinner
            message="Logging In"
            height={30}
            width={150}
            color="#ffffff"
            messageColor="#fff"
          />
        )}
        {(error.usernameError || error.passwordError) && (
          <p className="text-red-500">
            {error.usernameError || error.passwordError}
          </p>
        )}
      </form>
    </div>
  );
};

export default FacultyLogin;
