import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getCourseUnit,
  getScoreCourse,
} from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import { SET_ERRORS } from "../../../redux/actionTypes";
import { Link } from "react-router-dom";
// http://localhost:9090/api/admin/diem/lopTc/CNTT01-01
const Body = () => {
  const dispatch = useDispatch();
  const [course, setCourse] = useState("");
  const [error, setError] = useState({});
  const courses = useSelector((state) => state.admin.allCourse);
  console.log("courses", courses);
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    const CourseObj = courses.find((dp) => dp.maLop === course);
    const CourseId = CourseObj.maLopTc;

    dispatch(getScoreCourse(CourseId));
  };

  const scores = useSelector((state) => state.admin.scores.retObj);
  console.log("store", store);

  useEffect(() => {
    if (scores?.length !== 0) {
      setLoading(false);
    }
  }, [scores]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Danh sách Điểm</p>

      <div className="flex mt-4">
        <Link to="/admin/addscore" className="btn btn-primary">
          <button
            className="items-center gap-[9px] mr-4 w-[88px] h-[53px] hover:bg-[#04605E] block py-2 font-bold text-white rounded-lg px-4 
           bg-[#157572] focus:outline-none focus:shadow-outline "
          >
            Thêm
          </button>
        </Link>
        <button
          // onClick={dltSubject}
          className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
        >
          Xóa
        </button>
      </div>

      {/* ô search */}
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Lớp Tín Chỉ</label>

          <div className="flex">
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={course}
              onChange={(e) => setCourse(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {courses?.map((ut, idx) => (
                <MenuItem key={idx} value={ut.maLop}>
                  {ut.maLopTc}
                </MenuItem>
              ))}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>

      <div className="w-full">
        <div className="col-span-3">
          <div className={classes.loadingAndError}>
            {loading && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#111111"
                messageColor="blue"
              />
            )}
            {/* chỗ này tạm thời để vậy đã */}
            {courses?.length === 0 && (
              <p className="text-2xl font-bold text-red-500">
                Lớp tín chỉ chưa nhập điểm
              </p>
            )}
          </div>

          {search &&
            !loading &&
            Object.keys(error).length === 0 &&
            scores?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">Chọn</th>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">Mã Sinh Viên</th>
                    <th className="px-4 py-2">Chuyên Cần</th>
                    <th className="px-4 py-2">Giữa Kỳ</th>
                    <th className="px-4 py-2">Cuối Kỳ</th>
                    <th className="px-4 py-2">Trung Bình</th>
                    <th className="px-4 py-2">Xếp Loại</th>
                    <th className="px-4 py-2">Hành Động</th>
                  </tr>
                </thead>
                <tbody className="">
                  {scores?.map((score, idx) => (
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border">
                        <input
                          // onChange={handleInputChange}
                          // value={dep.id}
                          type="checkbox"
                        />
                      </td>
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">{score.maSv}</td>
                      <td className="px-4 py-2 border">{score.cc}</td>
                      <td className="px-4 py-2 border">{score.gk}</td>

                      <td className="px-4 py-2 border">{score.ck}</td>
                      <td className="px-4 py-2 border">{score.tb}</td>
                      <td className="px-4 py-2 border">{score.xepLoai}</td>
                      <td className="items-center justify-center px-4 py-2 mr-0 border">
                        <Link to={`/admin/teacher/${score._id}`}>
                          {/* /admin/teacher/:teacherCode */}
                          <button className="px-3.5 py-1 mr-5 font-bold text-white rounded hover:bg-[#04605E] bg-[#157572]  focus:outline-none focus:shadow-outline">
                            Xem
                          </button>
                        </Link>
                        <button className="px-3.5 py-1 font-bold text-white rounded  hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline">
                          Sửa
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
    </div>
  );
};

export default Body;
