import { Avatar } from "@mui/material";
import { format } from "date-fns";
import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

const Body = ({ studentObj }) => {
  return (
    <div className="mx-5 mt-1 item-center ">
      <div className="space-y-5">
        <div className="w-full min-h-[300px] py-8 px-7 text-center bg-[#fff] border rounded-md  shadow-md mx-auto flex">
          <div className="w-[220px] h-[220px] bg-[#DDDEEE] bg-opacity-50 rounded-full">
            <Avatar
              src={studentObj.hinhAnh}
              style={{ width: 220, height: 220 }}
            />
          </div>
          <div
            className="flex flex-row font-sans gap-x-3 "
            style={{ alignItems: "baseline" }}
          >
            <div
              className="flex flex-col font-sans gap-y-5"
              style={{ width: "130px", textAlign: "left" }}
            >
              <span className="font-sans">Mã sinh viên</span>
              <span className="font-sans">Họ và tên</span>
              <span className="font-sans">Giới tính</span>
              <span className="font-sans">Ngày Sinh</span>
              <span className="font-sans">Nơi sinh</span>
              <span className="font-sans">Địa chỉ</span>
              <span className="font-sans">Số điện thoại</span>
              <span className="font-sans">Email</span>
              <span className="font-sans">Lớp</span>
            </div>
            <div
              className="flex flex-col gap-y-5"
              style={{ width: "250px", textAlign: "left" }}
            >
              <span>{studentObj.maSv}</span>
              <span>
                {studentObj.ho} {studentObj.ten}
              </span>
              <span>{studentObj.phai}</span>
              <span>{studentObj.ngaySinh}</span>

              <span>{format(new Date(studentObj.ngaySinh), "dd/MM/yyyy")}</span>

              <span>{studentObj.noiSinh}</span>
              <span>{studentObj.diaChi}</span>
              <span>{studentObj.sdt}</span>
              <span>{studentObj.email}</span>
              <span>{studentObj.maLop}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Body;
