import React from "react";
import AssignmentIndIcon from "@mui/icons-material/AssignmentInd";
import SecurityUpdateIcon from "@mui/icons-material/SecurityUpdate";
import { Avatar } from "@mui/material";
import Data from "./Data";
import { useNavigate } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getTeacherById } from "../../../redux/actions/adminActions";

const Body = () => {
  const { id } = useParams();
  console.log("id", id);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getTeacherById(id));
  }, [dispatch, id]);

  const teacher = useSelector((state) => state.admin.teachers.data);

  console.log("teacher", teacher);

  return (
    <div className="flex-[0.8] mt-3">
      <div className="space-y-5">
        <div className="flex items-center space-x-2 text-gray-400">
          <div className="flex ml-5 space-x-2 text-gray-400">
            <AssignmentIndIcon />
            <h1>Profile</h1>
          </div>
        </div>
        <div className="w-[98%] bg-white relative rounded-xl">
          <div className="absolute left-[50%] top-[-10%]"></div>
          <div className="flex py-10 ml-10 space-x-40 h-[576px]">
            <div className="flex flex-col items-center space-y-5">
              <Avatar
                src={teacher.avatar}
                alt={teacher.name}
                sx={{ width: 150, height: 150 }}
              />
              <div className="text-center">
                <h2>{teacher.name}</h2>
                <p>{teacher.email}</p>
              </div>
            </div>
            <div className="flex flex-col space-y-10 ">
              <Data
                icon={<SecurityUpdateIcon />}
                title="Last login"
                value={teacher.last_login}
              />
              <Data
                icon={<AssignmentIndIcon />}
                title="Role"
                value={teacher.role}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Body;
