import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import { useParams } from "react-router-dom";
import { SET_ERRORS } from "../../../redux/actionTypes";
import { getThongkebysomething } from "../../../redux/actions/adminActions";
import {
  Bar,
  BarChart,
  CartesianGrid,
  LabelList,
  Legend,
  XAxis,
  YAxis,
} from "recharts";

import "./index.css";
const Body = () => {
  const dispatch = useDispatch();
  const [error, setError] = useState({});
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);
  const { maLopTc } = useParams();
  const [value, setValue] = useState({
    idLopTc: maLopTc,
    col: "",
  });
  // console.log("store", store);
  // http://localhost:9090/api/admin/diem/thong-ke?idLopTc=da6c6f34&col=XEPLOAI
  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    dispatch(
      getThongkebysomething({
        params: {
          ...value,
        },
      })
    );
  };

  const thongkes = useSelector((state) => state.admin.thongkes);
  console.log("thongkes", thongkes);

  const data = thongkes.map((item) => ({
    type: item.type,
    Bieu_Do: item.soLuong,
  }));

  console.log("data", data);
  useEffect(() => {
    if (thongkes?.length !== 0 || thongkes?.length === 0) {
      setLoading(false);
    }
  }, [thongkes]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Thống Kê</p>

      {/* ô search */}
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Biểu đồ Thống kê theo: </label>

          <div className="flex">
            <div>
              <Select
                required
                displayEmpty
                sx={{ height: 36 }}
                inputProps={{ "aria-label": "Without label" }}
                value={value.col}
                onChange={(e) => setValue({ ...value, col: e.target.value })}
                className={`${classes.InputStyle} hover:focus:border-none w-[166px] `}
              >
                <MenuItem value="">NONE</MenuItem>

                <MenuItem value="CC">Chuyên cần</MenuItem>
                <MenuItem value="GK">Giữa kỳ</MenuItem>
                <MenuItem value="CK">Kuoiws kỳ</MenuItem>
                <MenuItem value="TB">Trung bình</MenuItem>
                <MenuItem value="XEPLOAI">Xếp loại</MenuItem>
              </Select>
            </div>

            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>
      <div className={classes.loadingAndError}>
        {loading && data?.length !== 0 && (
          <Spinner
            message="Loading"
            height={50}
            width={150}
            color="#157572"
            messageColor="#157572"
          />
        )}
      </div>
      {search && data && (
        <div className="flex-[0.8] mt-3 mx-5 item-center">
          <BarChart width={1000} height={450} data={thongkes}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="type" />
            <YAxis
              label={{
                value: "Số lượng điểm",
                angle: -90,
                position: "insideLeft",
              }}
            />
            <Legend />

            <Bar
              dataKey="soLuong"
              fill="#8884d8"
              barSize={30}
              label={{
                position: "top",
              }}
              className=""
            />
          </BarChart>
        </div>
      )}
    </div>
  );
};

export default Body;
