import React from "react";

import { useEffect } from "react";
import useDebounce from "../../../hooks/useDebounce";

import { Bar, BarChart, CartesianGrid, Legend, XAxis, YAxis } from "recharts";

const data = [
  { name: "Điểm 0", ĐIỂM: 2 },
  // {name: "Điểm 0.1", ĐIỂM: 0}
  // .........................
  { name: "Điểm 1", ĐIỂM: 6 },
  { name: "Điểm 2", ĐIỂM: 3 },
  { name: "Điểm 3", ĐIỂM: 8 },
  { name: "Điểm 4", ĐIỂM: 4 },
  { name: "Điểm 5", ĐIỂM: 8 },
  { name: "Điểm 6", ĐIỂM: 8 },
  { name: "Điểm 7", ĐIỂM: 20 },
  { name: "Điểm 8", ĐIỂM: 8 },
  { name: "Điểm 9", ĐIỂM: 3 },
  { name: "Điểm 10", ĐIỂM: 10 },
];

const Body = () => {
  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <BarChart width={1000} height={450} data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis
          label={{ value: "Số lượng điểm", angle: -90, position: "insideLeft" }}
        />
        <Legend />
        <Bar dataKey="ĐIỂM" fill="#8884d8" barSize={30} />
      </BarChart>
    </div>
  );
};

