import React, { useEffect, useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import { useDispatch, useSelector } from "react-redux";
import FileBase from "react-file-base64";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import Spinner from "../../../utils/Spinner";
import { ADD_STUDENT, SET_ERRORS } from "../../../redux/actionTypes";
import * as classes from "../../../utils/styles";
import { Link } from "react-router-dom";
import { addStudent } from "../../../redux/actions/adminActions";
const Body = () => {
  const dispatch = useDispatch();
  const store = useSelector((state) => state);
  console.log("store", store);
  const units = useSelector((state) => state.admin.allUnit);
  const departments = useSelector((state) => state.admin.allDepartment);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState({});
  const [value, setValue] = useState({
    maSv: "",
    ho: "",
    ten: "",
    phai: "",
    ngaySinh: "",
    noiSinh: "",
    diaChi: "",
    sdt: "",
    email: "",
    maLop: "",
    maKhoa: "",
    hinhAnh: "",
  });

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setValue({ ...value, email: "" });
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setError({});
    setLoading(true);
    console.log("value", value);

    dispatch(addStudent(value));
  };

  useEffect(() => {
    if (store.errors || store.admin.studentAdded) {
      setLoading(false);
      if (store.admin.studentAdded) {
        setValue({
          maSv: "",
          ho: "",
          ten: "",
          phai: "",
          ngaySinh: "",
          noiSinh: "",
          diaChi: "",
          sdt: "",
          email: "",
          maLop: "",
          maKhoa: "",
          hinhAnh: "",
        });
        dispatch({ type: SET_ERRORS, payload: {} });
        dispatch({ type: ADD_STUDENT, payload: false });
      }
    } else {
      setLoading(true);
    }
  }, [store.errors, store.admin.studentAdded]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <div className="space-y-5">
        <div className="flex items-center space-x-2 text-gray-400">
          <AddIcon />
          <h1>Add Student</h1>
        </div>
        <Link to="/admin/student" className="btn btn-primary">
          <button className="mt-5 px-4 py-2  font-bold text-white rounded bg-[#157572] mr-14 hover:bg-[#04605E] focus:outline-none focus:shadow-outline">
            Quay lại
          </button>
        </Link>
        <div className="flex flex-col h-full pb-6 ml-3 mr-5 bg-white rounded-xl">
          <form
            className="w-full min-h-[300px] py-7 px-7 text-center bg-[#fff] border rounded-md  shadow-md mx-auto"
            onSubmit={handleSubmit}
          >
            <div className={classes.FormItem}>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Mã Sinh Viên :</h1>

                <input
                  placeholder="Mã Sinh Viên"
                  required
                  className={classes.adminInput}
                  type="text"
                  value={value.maSv}
                  onChange={(e) => setValue({ ...value, maSv: e.target.value })}
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Last Name :</h1>

                <input
                  placeholder="last name"
                  required
                  className={classes.adminInput}
                  type="text"
                  value={value.ho}
                  onChange={(e) => setValue({ ...value, ho: e.target.value })}
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>First Name :</h1>

                <input
                  placeholder="First Name"
                  required
                  className={classes.adminInput}
                  type="text"
                  value={value.ten}
                  onChange={(e) => setValue({ ...value, ten: e.target.value })}
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Ngày Sinh :</h1>

                <input
                  placeholder="Ngày Sinh"
                  required
                  className={classes.adminInput}
                  type="text"
                  value={value.ngaySinh}
                  onChange={(e) =>
                    setValue({ ...value, ngaySinh: e.target.value })
                  }
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Email :</h1>

                <input
                  placeholder="Email"
                  required
                  className={classes.adminInput}
                  type="email"
                  value={value.email}
                  onChange={(e) =>
                    setValue({ ...value, email: e.target.value })
                  }
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Lớp :</h1>
                <Select
                  required
                  displayEmpty
                  sx={{ height: 36 }}
                  inputProps={{ "aria-label": "Without label" }}
                  value={value.maLop}
                  onChange={(e) =>
                    setValue({ ...value, maLop: e.target.value })
                  }
                >
                  <MenuItem value="">None</MenuItem>
                  {units?.map((dp, idx) => (
                    <MenuItem key={idx} value={dp.maLop}>
                      {dp.tenLop}
                    </MenuItem>
                  ))}
                </Select>
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Department :</h1>
                <Select
                  required
                  displayEmpty
                  sx={{ height: 36 }}
                  inputProps={{ "aria-label": "Without label" }}
                  value={value.maKhoa}
                  onChange={(e) =>
                    setValue({ ...value, maKhoa: e.target.value })
                  }
                >
                  <MenuItem value="">None</MenuItem>
                  {departments?.map((dp, idx) => (
                    <MenuItem key={idx} value={dp.maKhoa}>
                      {dp.tenKhoa}
                    </MenuItem>
                  ))}
                </Select>
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Gender :</h1>
                <Select
                  required
                  displayEmpty
                  sx={{ height: 36 }}
                  inputProps={{ "aria-label": "Without label" }}
                  value={value.phai}
                  onChange={(e) => setValue({ ...value, phai: e.target.value })}
                >
                  <MenuItem value="">None</MenuItem>
                  <MenuItem value="Nam">Nam</MenuItem>
                  <MenuItem value="Nữ">Nữ</MenuItem>
                </Select>
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Nơi Sinh :</h1>

                <input
                  placeholder="Nơi Sinh"
                  required
                  className={classes.adminInput}
                  type="text"
                  value={value.noiSinh}
                  onChange={(e) =>
                    setValue({ ...value, noiSinh: e.target.value })
                  }
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Địa chỉ :</h1>

                <input
                  placeholder="Địa Chỉ"
                  required
                  className={classes.adminInput}
                  type="text"
                  value={value.diaChi}
                  onChange={(e) =>
                    setValue({ ...value, diaChi: e.target.value })
                  }
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Contact Number :</h1>

                <input
                  required
                  placeholder="Contact Number"
                  className={classes.adminInput}
                  type="text"
                  value={value.sdt}
                  onChange={(e) => setValue({ ...value, sdt: e.target.value })}
                />
              </div>
            </div>

            {/* hình ảnh */}
            <div className={classes.WrapImage}>
              <h1 className={classes.LabelStyle}>Avatar :</h1>

              <FileBase
                type="file"
                multiple={false}
                onDone={({ base64 }) => setValue({ ...value, hinhAnh: base64 })}
              />
            </div>
            {/* button */}
            <div className={classes.adminFormButton}>
              <button className={classes.adminFormSubmitButton} type="submit">
                Submit
              </button>
              <button
                onClick={() => {
                  setValue({
                    maSv: "",
                    ho: "",
                    ten: "",
                    phai: "",
                    ngaySinh: "",
                    noiSinh: "",
                    diaChi: "",
                    sdt: "",
                    email: "",
                    maLop: "",
                    maKhoa: "",
                    hinhAnh: "",
                  });
                  setError({});
                }}
                className={classes.adminFormClearButton}
                type="button"
              >
                Clear
              </button>
            </div>

            <div className={classes.loadingAndError}>
              {loading && (
                <Spinner
                  message="Adding Teacher"
                  height={30}
                  width={150}
                  color="#111111"
                  messageColor="blue"
                />
              )}
              {/* {(error.TeacherError || error.backendError) && (
                <p className="text-red-500">
                  {error.TeacherError || error.backendError}
                </p>
              )} */}
              {error.message ? (
                <p className="text-red-500">{error.message}</p>
              ) : null}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Body;
