import React, { useEffect, useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import { useDispatch, useSelector } from "react-redux";
import { addDepartment } from "../../../redux/actions/adminActions";
import Spinner from "../../../utils/Spinner";
import { ADD_DEPARTMENT, SET_ERRORS } from "../../../redux/actionTypes";
import * as classes from "../../../utils/styles";
import { Link } from "react-router-dom";
const Body = () => {
  const dispatch = useDispatch();
  const store = useSelector((state) => state);
  // const departments = useSelector((state) => state.admin.allDepartment);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState({});
  const [value, setValue] = useState({
    tenKhoa: "",
    maKhoa: "",
    sdt: "",
    email: "",
  });

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setValue({ ...value, email: "" });
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setError({});
    setLoading(true);
    dispatch(addDepartment(value));
  };

  useEffect(() => {
    if (store.errors || store.admin.departmentAdded) {
      setLoading(false);
      if (store.admin.departmentAdded) {
        setValue({
          tenKhoa: "",
          maKhoa: "",
          sdt: "",
          email: "",
        });
        dispatch({ type: SET_ERRORS, payload: {} });
        dispatch({ type: ADD_DEPARTMENT, payload: false });
      }
    } else {
      setLoading(true);
    }
  }, [store.errors, store.admin.departmentAdded]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3">
      <div className="space-y-5">
        <div className="flex items-center space-x-2 text-gray-400">
          <AddIcon />
          <h1>Add Department</h1>
        </div>
        <Link to="/admin/getalldepartment" className="btn btn-primary">
          <button className="block px-4 py-2 mt-5 ml-5 font-bold text-white rounded bg-primary mr-14 hover:bg-green-600 focus:outline-none focus:shadow-outline">
            Quay láº¡i
          </button>
        </Link>
        <div className="flex flex-col mr-10 bg-white rounded-xl">
          <form className={classes.adminForm0} onSubmit={handleSubmit}>
            {/* 1 */}
            <div className={`ml-[30%] ${classes.adminForm1}`}>
              <div className={classes.adminForm2l}>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>departmentName :</h1>

                  <input
                    placeholder="department Name"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.tenKhoa}
                    onChange={(e) =>
                      setValue({ ...value, tenKhoa: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Department Code :</h1>

                  <input
                    placeholder="Department Code"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.maKhoa}
                    onChange={(e) =>
                      setValue({ ...value, maKhoa: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>contactNumber :</h1>

                  <input
                    placeholder="contactNumber"
                    required
                    className={classes.adminInput}
                    type="text"
                    value={value.sdt}
                    onChange={(e) =>
                      setValue({ ...value, sdt: e.target.value })
                    }
                  />
                </div>
                <div className={classes.adminForm3}>
                  <h1 className={classes.adminLabel}>Email :</h1>

                  <input
                    placeholder="Email"
                    required
                    className={classes.adminInput}
                    type="email"
                    value={value.email}
                    onChange={(e) =>
                      setValue({ ...value, email: e.target.value })
                    }
                  />
                </div>
              </div>
            </div>
            {/* 2 submit and clear*/}
            <div className={classes.adminFormButton}>
              <button className={classes.adminFormSubmitButton} type="submit">
                Submit
              </button>
              <button
                onClick={() => {
                  setValue({
                    tenKhoa: "",
                    maKhoa: "",
                    sdt: "",
                    email: "",
                  });
                  setError({});
                }}
                className={classes.adminFormClearButton}
                type="button"
              >
                Clear
              </button>
            </div>
            {/* 3 loading */}
            <div className={classes.loadingAndError}>
              {loading && (
                <Spinner
                  message="Adding Department"
                  height={30}
                  width={150}
                  color="#111111"
                  messageColor="blue"
                />
              )}
              {store.error && <p className="text-red-500">{store.error}</p>}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Body;
