import React, { useEffect, useState } from "react";
import EngineeringIcon from "@mui/icons-material/Engineering";
import { useDispatch, useSelector } from "react-redux";
import { getAllTeacher, getTeacher } from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import { SET_ERRORS } from "../../../redux/actionTypes";
const Body = () => {
  const dispatch = useDispatch();
  const [department, setDepartment] = useState("");
  const [error, setError] = useState({});
  const departments = useSelector((state) => state.admin.allDepartment);
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   setSearch(true);
  //   setLoading(true);
  //   setError({});
  //   dispatch(getTeacher({ departmentId }));
  //   // dispatch(getAllTeacher());
  // };
  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    const departmentObj = departments.find(
      (dp) => dp.departmentName === department
    );
    const departmentId = departmentObj._id;
    console.log("departmentId", departmentId);
    dispatch(getTeacher(departmentId));
  };

  const teachers = useSelector((state) => state.admin.teachers.data);

  console.log("teacher", teachers);
  useEffect(() => {
    if (teachers?.length !== 0) {
      setLoading(false);
    }
  }, [teachers]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3">
      <div className="space-y-5">
        <div className="flex items-center space-x-2 text-gray-400">
          <EngineeringIcon />
          <h1>All Teacher</h1>
        </div>
        <div className=" mr-10 bg-white grid grid-cols-4 rounded-xl pt-6 pl-6 h-[29.5rem]">
          <form
            className="flex flex-col col-span-1 space-y-2"
            onSubmit={handleSubmit}
          >
            <label htmlFor="department">Department</label>
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={department}
              onChange={(e) => setDepartment(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {departments?.map((dp, idx) => (
                <MenuItem key={idx} value={dp.departmentName}>
                  {dp.departmentName}
                </MenuItem>
              ))}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56`}
              type="submit"
            >
              Search
            </button>
          </form>
          <div className="col-span-3 mr-6">
            <div className={classes.loadingAndError}>
              {loading && (
                <Spinner
                  message="Loading"
                  height={50}
                  width={150}
                  color="#111111"
                  messageColor="blue"
                />
              )}
              {(error.noTeacherError || error.backendError) && (
                <p className="text-2xl font-bold text-red-500">
                  {error.noTeacherError || error.backendError}
                </p>
              )}
            </div>

            {search &&
              !loading &&
              Object.keys(error).length === 0 &&
              teachers?.length !== 0 && (
                <div className={classes.adminData}>
                  <div className="grid grid-cols-12">
                    <h1 className={`${classes.adminDataHeading} col-span-1 `}>
                      Sr no.
                    </h1>
                    <h1 className={`${classes.adminDataHeading} col-span-3 `}>
                      Name
                    </h1>
                    <h1 className={`${classes.adminDataHeading} col-span-2 `}>
                      Username
                    </h1>
                    <h1 className={`${classes.adminDataHeading} col-span-3 `}>
                      Email
                    </h1>
                    <h1 className={`${classes.adminDataHeading} col-span-3 `}>
                      Designation
                    </h1>
                  </div>
                  {teachers?.map((fac, idx) => (
                    <div
                      key={idx}
                      className={`${classes.adminDataBody} grid-cols-12`}
                    >
                      <h1
                        className={`${classes.adminDataBodyFields} font-bold border-0 col-span-1`}
                      >
                        {idx + 1}
                      </h1>
                      <h1
                        className={`col-span-3 ${classes.adminDataBodyFields}`}
                      >
                        {fac.avatar}
                      </h1>
                      {/* <h1
                        className={`col-span-2 ${classes.adminDataBodyFields} `}
                      >
                        {fac.username}
                      </h1>
                      <h1
                        className={`col-span-3 ${classes.adminDataBodyFields}`}
                      >
                        {fac.email}
                      </h1>
                      <h1
                        className={`col-span-3 ${classes.adminDataBodyFields}`}
                      >
                        {fac.designation}
                      </h1> */}
                    </div>
                  ))}
                </div>
              )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Body;
