import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getAllStudent,
  getStudentUnit,
  updateStudent,
} from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import { SET_ERRORS, UPDATE_STUDENT } from "../../../redux/actionTypes";
import { Link } from "react-router-dom";
import FileBase from "react-file-base64";

import ReactModal from "react-modal";
// http://localhost:9090/api/admin/sinhVien/lop/CNTT01
const modalStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};
const Body = () => {
  const dispatch = useDispatch();
  const [unit, setUnit] = useState("");
  const [error, setError] = useState({});
  const units = useSelector((state) => state.admin.allUnit);
  const departments = useSelector((state) => state.admin.allDepartment);
  console.log("units", units);
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);

  //begin edit
  const [value, setValue] = useState({
    id: "",
    maSv: "",
    ho: "",
    ten: "",
    phai: "",
    ngaySinh: "",
    noiSinh: "",
    diaChi: "",
    trangThai: null,
    sdt: "",
    email: "",
    maLop: "",
    maKhoa: "",
    hinhAnh: "",
  });
  const [selectedStudent, setSelectedStudent] = useState("");
  console.log("selectedStudent", selectedStudent);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };
  const handleEditClick = (student) => {
    setSelectedStudent(student);
    setIsModalOpen(true);
    setValue({
      id: student.id,
      maSv: student.maSv,
      ho: "",
      ten: "",
      phai: "",
      ngaySinh: "",
      noiSinh: "",
      diaChi: "",
      trangThai: student.trangThai,
      sdt: "",
      email: student.email,
      maLop: "",
      maKhoa: "",
      hinhAnh: "",
    });
  };
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!value.tenKhoa && !value.sdt) {
      alert("Enter at least one value");
    } else {
      const updatedValue = {};
      if (value.ho !== "") {
        updatedValue.ho = value.ho;
      } else {
        updatedValue.ho = selectedStudent.ho;
      }
      if (value.ten !== "") {
        updatedValue.ten = value.ten;
      } else {
        updatedValue.ten = selectedStudent.ten;
      }
      if (value.phai !== "") {
        updatedValue.phai = value.phai;
      } else {
        updatedValue.phai = selectedStudent.phai;
      }
      if (value.ngaySinh !== "") {
        updatedValue.ngaySinh = value.ngaySinh;
      } else {
        updatedValue.ngaySinh = selectedStudent.ngaySinh;
      }
      if (value.noiSinh !== "") {
        updatedValue.noiSinh = value.noiSinh;
      } else {
        updatedValue.noiSinh = selectedStudent.noiSinh;
      }
      if (value.diaChi !== "") {
        updatedValue.diaChi = value.diaChi;
      } else {
        updatedValue.diaChi = selectedStudent.diaChi;
      }
      if (value.sdt !== "") {
        updatedValue.sdt = value.sdt;
      } else {
        updatedValue.sdt = selectedStudent.sdt;
      }
      if (value.maLop !== "") {
        updatedValue.maLop = value.maLop;
      } else {
        updatedValue.maLop = selectedStudent.maLop;
      }
      if (value.maKhoa !== "") {
        updatedValue.maKhoa = value.maKhoa;
      } else {
        updatedValue.maKhoa = selectedStudent.maKhoa;
      }
      if (value.hinhAnh !== "") {
        updatedValue.hinhAnh = value.hinhAnh;
      } else {
        updatedValue.hinhAnh = selectedStudent.hinhAnh;
      }
      dispatch(updateStudent({ ...selectedStudent, ...updatedValue }));
      dispatch({ type: UPDATE_STUDENT, payload: false });
    }
    closeModal();
  };
  useEffect(() => {
    dispatch(getAllStudent());
  }, [dispatch, store.errors, store.admin.updatedStudent]);

  //-------------------------------------
  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    const UnitObj = units.find((dp) => dp.tenLop === unit);
    const UnitId = UnitObj.maLop;
    dispatch(getStudentUnit(UnitId));
  };

  const students = useSelector((state) => state.admin.students.retObj);
  console.log("store", store);

  useEffect(() => {
    if (students?.length !== 0) {
      setLoading(false);
    }
  }, [students]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Danh sách sinh viên</p>

      <div className="flex mt-4">
        <Link to="/admin/addstudent" className="btn btn-primary">
          <button
            className="items-center gap-[9px] mr-4 w-[88px] h-[53px] hover:bg-[#04605E] block py-2 font-bold text-white rounded-lg px-4 
           bg-[#157572] focus:outline-none focus:shadow-outline "
          >
            Thêm
          </button>
        </Link>
        <button
          // onClick={dltSubject}
          className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
        >
          Xóa
        </button>
      </div>

      {/* ô search */}
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Lớp</label>

          <div className="flex">
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={unit}
              onChange={(e) => setUnit(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {units?.map((ut, idx) => (
                <MenuItem key={idx} value={ut.tenLop}>
                  {ut.tenLop}
                </MenuItem>
              ))}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>

      <div className="w-full">
        <div className="col-span-3">
          <div className={classes.loadingAndError}>
            {loading && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#111111"
                messageColor="blue"
              />
            )}
            {/* chỗ này tạm thời để vậy đã */}
            {students?.length === 0 && (
              <p className="text-2xl font-bold text-red-500">
                Lớp chưa có giảng viên
              </p>
            )}
          </div>

          {search &&
            !loading &&
            Object.keys(error).length === 0 &&
            students?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">Chọn</th>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">Mã Sinh Viên</th>
                    <th className="px-4 py-2">Họ</th>
                    <th className="px-4 py-2">Tên</th>

                    <th className="px-4 py-2">Email</th>
                    <th className="px-4 py-2">Hành động</th>
                  </tr>
                </thead>
                <tbody className="">
                  {students?.map((student, idx) => (
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border">
                        <input
                          // onChange={handleInputChange}
                          // value={dep.id}
                          type="checkbox"
                        />
                      </td>
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">{student.maSv}</td>
                      <td className="px-4 py-2 border">{student.ho}</td>
                      <td className="px-4 py-2 border">{student.ten}</td>

                      <td className="px-4 py-2 border">{student.email}</td>

                      <td className="items-center justify-center px-4 py-2 mr-0 border">
                        <Link to={`/admin/teacher/${student._id}`}>
                          {/* /admin/teacher/:teacherCode */}
                          <button className="px-3.5 py-1 mr-5 font-bold text-white rounded hover:bg-[#04605E] bg-[#157572]  focus:outline-none focus:shadow-outline">
                            Xem
                          </button>
                        </Link>
                        <button
                          className="px-3.5 py-1 font-bold text-white rounded  hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                          onClick={() => handleEditClick(student)}
                        >
                          Sửa
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
      {selectedStudent ? (
        <ReactModal
          isOpen={isModalOpen}
          onRequestClose={openModal}
          style={modalStyles}
          ariaHideApp={false}
        >
          {/* <UpdateStudent
            selectedStudent={selectedStudent}
            value={value}
            setValue={setValue}
            closeModal={closeModal}
          ></UpdateStudent> */}
          <div className=" mr-10 bg-white flex flex-col rounded-xl overflow-y-scroll h-[27rem] ">
            <form className={classes.adminForm0} onSubmit={handleFormSubmit}>
              <div className={classes.adminForm1}>
                <div className={classes.adminForm2l}>
                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Mã Sinh Viên:</h1>
                    <input
                      placeholder={selectedStudent.maSv}
                      disabled
                      className={classes.adminInput}
                      type="text"
                    />
                  </div>

                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}> Họ:</h1>
                    <input
                      placeholder={selectedStudent.ho}
                      className={classes.adminInput}
                      type="text"
                      value={value.ho}
                      onChange={(e) =>
                        setValue({ ...value, ho: e.target.value })
                      }
                    />
                  </div>

                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Tên :</h1>
                    <input
                      placeholder={selectedStudent.ten}
                      className={classes.adminInput}
                      type="text"
                      value={value.ten}
                      onChange={(e) =>
                        setValue({ ...value, ten: e.target.value })
                      }
                    />
                  </div>

                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Phái :</h1>
                    <input
                      placeholder={selectedStudent.phai}
                      className={classes.adminInput}
                      type="text"
                      value={value.phai}
                      onChange={(e) =>
                        setValue({ ...value, phai: e.target.value })
                      }
                    />
                  </div>

                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Ngày Sinh :</h1>
                    <input
                      placeholder={selectedStudent.ngaySinh}
                      className={classes.adminInput}
                      type="text"
                      value={value.ngaySinh}
                      onChange={(e) =>
                        setValue({ ...value, ngaySinh: e.target.value })
                      }
                    />
                  </div>

                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Nơi Sinh :</h1>
                    <input
                      placeholder={selectedStudent.noiSinh}
                      className={classes.adminInput}
                      type="text"
                      value={value.noiSinh}
                      onChange={(e) =>
                        setValue({ ...value, noiSinh: e.target.value })
                      }
                    />
                  </div>

                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Địa Chỉ :</h1>
                    <input
                      placeholder={selectedStudent.diaChi}
                      className={classes.adminInput}
                      type="text"
                      value={value.diaChi}
                      onChange={(e) =>
                        setValue({ ...value, diaChi: e.target.value })
                      }
                    />
                  </div>

                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Số Điện thoại :</h1>
                    <input
                      placeholder={selectedStudent.sdt}
                      className={classes.adminInput}
                      type="text"
                      value={value.sdt}
                      onChange={(e) =>
                        setValue({ ...value, sdt: e.target.value })
                      }
                    />
                  </div>

                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Email :</h1>
                    <input
                      placeholder={selectedStudent.result?.email}
                      disabled
                      className={classes.adminInput}
                      type="text"
                    />
                  </div>
                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Lớp :</h1>
                    <Select
                      required
                      displayEmpty
                      sx={{ height: 36 }}
                      inputProps={{ "aria-label": "Without label" }}
                      value={value.maLop}
                      onChange={(e) =>
                        setValue({ ...value, maLop: e.target.value })
                      }
                    >
                      <MenuItem value="">None</MenuItem>
                      {units?.map((dp, idx) => (
                        <MenuItem key={idx} value={dp.maLop}>
                          {dp.tenLop}
                        </MenuItem>
                      ))}
                    </Select>
                  </div>
                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Department :</h1>
                    <Select
                      displayEmpty
                      sx={{ height: 36 }}
                      inputProps={{ "aria-label": "Without label" }}
                      value={value.department}
                      onChange={(e) =>
                        setValue({ ...value, department: e.target.value })
                      }
                    >
                      <MenuItem value="">None</MenuItem>
                      {departments?.map((dp, idx) => (
                        <MenuItem key={idx} value={dp.department}>
                          {dp.department}
                        </MenuItem>
                      ))}
                    </Select>
                  </div>
                  <div className={classes.adminForm3}>
                    <h1 className={classes.adminLabel}>Avatar :</h1>
                    <FileBase
                      type="file"
                      multiple={false}
                      onDone={({ base64 }) =>
                        setValue({ ...value, avatar: base64 })
                      }
                    />
                  </div>
                </div>
                <div className={classes.adminFormButton}>
                  <button
                    className={classes.adminFormSubmitButton}
                    type="submit"
                  >
                    Submit
                  </button>
                  <Link to="/admin/student" className="btn btn-primary">
                    <button
                      className={classes.adminFormClearButton}
                      type="button"
                      onClick={closeModal}
                    >
                      Cancel
                    </button>
                  </Link>
                </div>
              </div>
              <div className={classes.loadingAndError}>
                {loading && (
                  <Spinner
                    message="Updating"
                    height={30}
                    width={150}
                    color="#111111"
                    messageColor="blue"
                  />
                )}
              </div>
            </form>
          </div>
        </ReactModal>
      ) : null}
    </div>
  );
};

export default Body;
