import React from "react";
import LogoutIcon from "@mui/icons-material/Logout";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import Swal from "sweetalert2";

const Header = () => {
  const user = JSON.parse(
    localStorage.getItem("teacherUser" || "studentUser" || "adminUser")
  );
  console.log("user", user);
  const role = user.retObj.roles[0];
  console.log("role", role);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const logout = () => {
    Swal.fire({
      title: "Are you sure?",
      text: "You won't be able to revert this!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Yes, sign out!",
    }).then(async (result) => {
      if (result.isConfirmed) {
        dispatch({ type: "LOGOUT" });
        navigate("/");
      }
    });
  };
  return (
    <div className="flex bg-[#FFFFFF] items-center justify-between  h-[74px] w-full">
      <div className="flex items-center">
        <h1 className="ml-5 text-lg font-bold text-red-600">
          {" "}
          Học Viện Công Nghệ Bưu Chính Viễn Thông
        </h1>
      </div>
      <div className="flex items-center mx-5 space-x-3">
        {role === "ROLE_SINHVIEN" && (
          <>
            <h1 className="text-[#9ca3af] font-serif">Sinh viên</h1>
            <LogoutIcon
              onClick={logout}
              className="transition-all cursor-pointer hover:scale-125 text-[#9ca3af] "
            />
          </>
        )}
        {role === "ROLE_ADMIN" && (
          <>
            <h1 className="text-[#9ca3af] font-serif">Admin</h1>
            <LogoutIcon
              onClick={logout}
              className="transition-all cursor-pointer hover:scale-125 text-[#9ca3af] "
            />
          </>
        )}
        {role === "ROLE_GIANGVIEN" && (
          <>
            <h1 className="text-[#9ca3af] font-serif">Giảng viên</h1>
            <LogoutIcon
              onClick={logout}
              className="transition-all cursor-pointer hover:scale-125 text-[#9ca3af] "
            />
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
