import {
  GET_ALL_DEPARTMENT,
  GET_TEACHER_BY_ID,
  GET_TKB,
  LOGOUT,
} from "../actionTypes";

const initialState = {
  teachers: [],
  tkb: [],
};

const teacherReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGOUT:
      localStorage.clear();
      return { ...state, authData: null };
    case GET_ALL_DEPARTMENT:
      return {
        ...state,
        allDepartment: action.payload,
      };

    case GET_TEACHER_BY_ID:
      return {
        ...state,
        teachers: action.payload,
      };
    case GET_TKB:
      return {
        ...state,
        tkb: action.payload,
      };
    default:
      return state;
  }
};

export default teacherReducer;
