import { LOGIN, SET_ERRORS } from "../actionTypes";
import * as api from "../api";

export const userLogin = (formData, navigate) => async (dispatch) => {
  try {
    const { data } = await api.userLogin(formData);
    dispatch({ type: LOGIN, data });

    if (data.retObj.roles === "ROLE_SINHVIEN") navigate("/admin/studentHome");
    else if (data.retObj.roles === "ROLE_GIANGVIEN") navigate("/admin/home");
    else navigate("/admin/teacherHome");
  } catch (error) {
    dispatch({ type: SET_ERRORS, payload: error.response.retObj });
  }
};
