import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  deleteDangkymon,
  getAllCoursebyMKH,
  getScoreCourse,
  updateScore,
} from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import {
  DELETE_DANGKYMON,
  SET_ERRORS,
  UPDATE_SCORE,
} from "../../../redux/actionTypes";
import { Link } from "react-router-dom";
import ReactModal from "react-modal";
// http://localhost:9090/api/admin/diem/lopTc/CNTT01-01
const modalStyles = {
  content: {
    top: "45%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "0",
    border: "none",
  },
};
const Body = () => {
  const dispatch = useDispatch();
  const [course, setCourse] = useState("");
  const [error, setError] = useState({});
  // const courses = useSelector((state) => state.admin.allCourse);
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);
  // chức năng mới
  const [valueMKH, setValueMKH] = useState({ maKeHoach: "" });
  const [isLoading, setIsLoading] = useState(false);
  console.log("valueMKH", valueMKH);
  useEffect(() => {
    // if (valueMKH.idKeHoachNam !== "") {
    //   setIsLoading(true);
    //   fetch(
    //     `http://localhost:9090/api/admin/dsLopTc?maKeHoach=${valueMKH.idKeHoachNam}`
    //   )
    //     .then((response) => response.json())
    //     .then((data) => {
    //       setCourses(data);
    //       setIsLoading(false);
    //     })
    //     .catch((error) => {
    //       console.error("Error fetching courses:", error);
    //       setIsLoading(false);
    //     });
    // }

    setIsLoading(true);
    // setLoading(true);
    setError({});
    dispatch(
      getAllCoursebyMKH({
        params: {
          ...valueMKH,
        },
      })
    );
  }, [valueMKH.maKeHoach]);

  console.log("store", store);
  const courses = useSelector((state) => state.admin.courses);
  useEffect(() => {
    if (courses?.length !== 0) {
      setIsLoading(false);
    }
  }, [courses]);

  console.log("courses", courses);
  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    const CourseObj = courses?.find((dp) => dp.maLopTc === course);
    const CourseId = CourseObj?.maLopTc; // cần mã lớp tín chỉ

    dispatch(getScoreCourse(CourseId));
  };

  const scores = useSelector((state) => state.admin.scores.retObj);

  // chỉnh sau
  useEffect(() => {
    if (scores?.length !== 0 || scores?.length === 0) {
      setLoading(false);
    }
  }, [scores]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  //BEGIN EDIT
  const [value, setValue] = useState({
    id: "",
    cc: "",
    gk: "",
    ck: "",
    tb: "",
    xepLoai: "",
    maSv: "",
    maLopTc: "",
  });
  const [selectedScore, setSelectedScore] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalMode, setModalMode] = useState(null);

  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };
  const handleEditClick = (score) => {
    setSelectedScore(score);
    setIsModalOpen(true);
    setModalMode("edit");
    setValue({
      id: score.id,
      cc: "",
      gk: "",
      ck: "",
      tb: "",
      xepLoai: "",
      maSv: score.maSv,
      maLopTc: score.maLopTc,
    });
  };
  const handleFormSubmit = (e) => {
    e.preventDefault();
    const updatedValue = {};

    if (value.cc !== "") {
      updatedValue.cc = value.cc;
    } else {
      updatedValue.cc = selectedScore.cc;
    }
    if (value.gk !== "") {
      updatedValue.gk = value.gk;
    } else {
      updatedValue.gk = selectedScore.gk;
    }
    if (value.ck !== "") {
      updatedValue.ck = value.ck;
    } else {
      updatedValue.ck = selectedScore.ck;
    }
    if (value.tb !== "") {
      updatedValue.tb = value.tb;
    } else {
      updatedValue.tb = selectedScore.tb;
    }
    if (value.xepLoai !== "") {
      updatedValue.xepLoai = value.xepLoai;
    } else {
      updatedValue.xepLoai = selectedScore.xepLoai;
    }

    dispatch(updateScore({ ...selectedScore, ...updatedValue }));
    dispatch({ type: UPDATE_SCORE, payload: false });
    closeModal();
  };

  useEffect(() => {
    dispatch(getScoreCourse(selectedScore.maLopTc));
  }, [dispatch, store.errors, store.admin.updateScore]);
  //ENDEDIT
  // BEGIN DELETE
  const [checkedValue, setCheckedValue] = useState([]);
  // check xóa
  const handleInputChange = (e) => {
    const tempCheck = checkedValue;
    let index;
    if (e.target.checked) {
      tempCheck.push(e.target.value);
    } else {
      index = tempCheck.indexOf(e.target.value);
      tempCheck.splice(index, 1);
    }
    setCheckedValue(tempCheck);
  };
  // hàm xóa
  const dltSubject = (e) => {
    dispatch(deleteDangkymon(checkedValue));
  };
  // câp nhật lại
  useEffect(() => {
    if (store.admin.dangkymonDeleted) {
      setCheckedValue([]);
      const CourseObj = courses.find((dp) => dp.maLopTc === course);
      const CourseId = CourseObj.maLopTc; // cần mã lớp tín chỉ
      dispatch(getScoreCourse(CourseId));
      dispatch({ type: DELETE_DANGKYMON, payload: false });
    }
  }, [store.admin.dangkymonDeleted]);
  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Danh sách Điểm</p>

      <div className="flex mt-4">
        {scores && scores?.length !== 0 && (
          <button
            onClick={dltSubject}
            className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
          >
            Xóa
          </button>
        )}
        {/* <button
          onClick={dltSubject}
          className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
        >
          Xóa
        </button> */}
      </div>

      {/* ô search */}
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Lớp Tín Chỉ</label>

          <div className="flex">
            <Select
              displayEmpty
              sx={{ height: 36 }}
              inputProps={{ "aria-label": "Without label" }}
              value={valueMKH.maKeHoach}
              onChange={(e) =>
                setValueMKH({ ...valueMKH, maKeHoach: e.target.value })
              }
              className=" h-10  bg-[#DDDEEE] bg-opacity-50 rounded-md outline-none text-sm hover:focus:border-none"
            >
              <MenuItem value="">None</MenuItem>
              <MenuItem value="MKH1">MKH1</MenuItem>
              <MenuItem value="MKH0">MKH0</MenuItem>
            </Select>
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={course}
              onChange={(e) => setCourse(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {isLoading ? (
                <MenuItem disabled>Loading...</MenuItem>
              ) : (
                courses.map((ut, idx) => (
                  <MenuItem key={idx} value={ut.maLopTc}>
                    {ut.maLopTc}
                  </MenuItem>
                ))
              )}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>
      {/* <div className="mt-10">
        <form className="" onSubmit={handleSubmit}>
          <div className={classes.FormItem}>
            <div className={classes.WrapInputLabel}>
              <h1 className={classes.LabelStyle}>Kế hoạch năm *:</h1>
              <Select
                displayEmpty
                sx={{ height: 36 }}
                inputProps={{ "aria-label": "Without label" }}
                value={value.idKeHoachNam}
                onChange={(e) =>
                  setValue({ ...value, idKeHoachNam: e.target.value })
                }
                className=" h-10  bg-[#DDDEEE] bg-opacity-50 rounded-md outline-none text-sm hover:focus:border-none"
              >
                <MenuItem value="">None</MenuItem>
                <MenuItem value="c2b42960">MKH1</MenuItem>
              </Select>
            </div>
            <div className={classes.WrapInputLabel}>
              <h1 className={classes.LabelStyle}>Tìm môn học:</h1>
              <input
                placeholder="Tên môn học"
                className={classes.InputStyle}
                type="text"
                value={value.keySearch}
                onChange={(e) =>
                  setValue({ ...value, keySearch: e.target.value })
                }
              />
            </div>
            <div className={classes.WrapInputLabel}>
              <button
                className="mt-7 items-center my-auto bg-red-500 w-56 h-[40px] rounded-md text-white hover:scale-105 hover:bg-red-700 transition-all duration-200"
                type="submit"
              >
                Search
              </button>
            </div>
          </div>
        </form>
      </div> */}
      <div className="w-full">
        <div className="col-span-3">
          <div className={classes.loadingAndError}>
            {loading && scores?.length !== 0 && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#157572"
                messageColor="#157572"
              />
            )}
            {/* chỗ này tạm thời để vậy đã */}
            {scores?.length === 0 && (
              <p className="text-2xl font-bold text-red-500">
                Lớp tín chỉ chưa nhập điểm
              </p>
            )}
          </div>

          {search &&
            !loading &&
            Object.keys(error).length === 0 &&
            scores?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">Chọn</th>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">Mã Sinh Viên</th>
                    <th className="px-4 py-2">Chuyên Cần</th>
                    <th className="px-4 py-2">Giữa Kỳ</th>
                    <th className="px-4 py-2">Cuối Kỳ</th>
                    <th className="px-4 py-2">Trung Bình</th>
                    <th className="px-4 py-2">Xếp Loại</th>
                    <th className="px-4 py-2">Hành Động</th>
                  </tr>
                </thead>
                <tbody className="">
                  {scores?.map((score, idx) => (
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border">
                        <input
                          onChange={handleInputChange}
                          value={score.id}
                          type="checkbox"
                        />
                      </td>
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">{score.maSv}</td>
                      <td className="px-4 py-2 border">{score.cc}</td>
                      <td className="px-4 py-2 border">{score.gk}</td>

                      <td className="px-4 py-2 border">{score.ck}</td>
                      <td className="px-4 py-2 border">{score.tb}</td>
                      <td className="px-4 py-2 border">{score.xepLoai}</td>
                      <td
                        className="items-center justify-center px-4 py-2 mr-0 border"
                        style={{ display: "flex", justifyContent: "center" }}
                      >
                        <button
                          className="px-3.5 py-1 font-bold text-white rounded  hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                          onClick={() => handleEditClick(score)}
                        >
                          Sửa
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
      {selectedScore ? (
        <ReactModal
          isOpen={isModalOpen}
          onRequestClose={openModal}
          style={modalStyles}
          ariaHideApp={false}
        >
          <div className="flex flex-col bg-white rounded-xl">
            <form
              className="w-full min-h-[300px] py-10 px-7 text-center bg-[#fff] border rounded-md  shadow-md mx-auto"
              onSubmit={handleFormSubmit}
            >
              <div className="grid grid-cols-3 gap-x-10">
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Mã sinh viên:</h1>
                  <input
                    placeholder={selectedScore?.maSv}
                    disabled
                    className={classes.InputStyle}
                    type="text"
                    value={value.maSv}
                  />
                </div>

                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Chuyên Cần :</h1>
                  <input
                    placeholder={selectedScore?.cc}
                    className={classes.InputStyle}
                    type="number"
                    value={value.cc}
                    onChange={(e) =>
                      setValue({
                        ...value,
                        cc: e.target.value,
                      })
                    }
                  />
                </div>

                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Giữa kỳ :</h1>
                  <input
                    placeholder={selectedScore?.gk}
                    className={classes.InputStyle}
                    type="number"
                    value={value.gk}
                    onChange={(e) =>
                      setValue({
                        ...value,
                        gk: e.target.value,
                      })
                    }
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Cuối kỳ :</h1>
                  <input
                    placeholder={selectedScore?.ck}
                    className={classes.InputStyle}
                    type="number"
                    value={value.ck}
                    onChange={(e) =>
                      setValue({
                        ...value,
                        ck: e.target.value,
                      })
                    }
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Trung bình :</h1>
                  <input
                    placeholder={selectedScore?.tb}
                    className={classes.InputStyle}
                    type="number"
                    value={value.tb}
                    onChange={(e) =>
                      setValue({
                        ...value,
                        tb: e.target.value,
                      })
                    }
                  />
                </div>
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Xếp loại :</h1>

                  <Select
                    required
                    displayEmpty
                    sx={{ height: 36 }}
                    inputProps={{ "aria-label": "Without label" }}
                    // value={value.phai}
                    value={value.xepLoai || selectedScore.xepLoai}
                    onChange={(e) =>
                      setValue({ ...value, xepLoai: e.target.value })
                    }
                    className={classes.InputStyle}
                  >
                    <MenuItem value="Xuất sắc">Xuất sắc</MenuItem>
                    <MenuItem value="Giỏi">Giỏi</MenuItem>
                    <MenuItem value="Khá">Khá</MenuItem>
                    <MenuItem value="Trung bình">Trung bình</MenuItem>
                    <MenuItem value="Yếu">Yếu</MenuItem>
                  </Select>
                </div>
              </div>
              <div className={classes.adminFormButton}>
                <button className={classes.adminFormSubmitButton} type="submit">
                  Lưu
                </button>
                <Link to="/admin/allscore" className="btn btn-primary">
                  <button
                    className={classes.adminFormClearButton}
                    type="button"
                    onClick={closeModal}
                  >
                    Thoát
                  </button>
                </Link>
              </div>
            </form>
          </div>
        </ReactModal>
      ) : null}
    </div>
  );
};

export default Body;
