import React from "react";
import ReactModal from "react-modal";
const modalStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "0",
  },
};
const DetailStudent = ({ student, isOpen, onClose }) => {
  return (
    <ReactModal
      //   style={style}
      //   ariaHideApp={false}
      isOpen={isOpen}
      onRequestClose={onClose}
    >
      <div className="items-center justify-center">
        <div className="w-[808px] min-h-[100px] py-7 px-7 text-center bg-[#fff] border rounded-md m-6 shadow-md mx-auto pb-20">
          <div className="flex mt-10 mb-10 gap-x-16">
            {/* hình ảnh */}
            <div className="w-[220px] h-[220px] bg-[#DDDEEE] bg-opacity-50 rounded-full">
              <img
                src="https://scontent.fsgn5-2.fna.fbcdn.net/v/t1.6435-9/117676547_613882612900154_7191648885743595046_n.jpg?_nc_cat=105&ccb=1-7&_nc_sid=174925&_nc_ohc=JaJ0UYjjwKwAX_E5ubv&_nc_ht=scontent.fsgn5-2.fna&oh=00_AfA58Y4knFdcrXsy3zbXjkALMCbUGZobgMiceZ-nAw7E_Q&oe=645B279F"
                alt=""
                className="w-[220px] h-[220px] bg-[#DDDEEE] bg-opacity-50 rounded-full"
              />
            </div>
            {/* thông tin chi tiết */}
            <div
              className="flex flex-row font-sans gap-x-3 "
              style={{ alignItems: "baseline" }}
            >
              <div
                className="flex flex-col font-sans gap-y-5"
                style={{ width: "130px", textAlign: "left" }}
              >
                <span className="font-sans">Mã sinh viên</span>
                <span className="font-sans">Họ và tên</span>
                <span className="font-sans">Giới tính</span>
                <span className="font-sans">Ngày Sinh</span>
                <span className="font-sans">Nơi sinh</span>
                <span className=""> </span>
                <span className="font-sans">Địa chỉ</span>
                <span> </span>
                <span className="font-sans">Số điện thoại</span>
                <span className="font-sans">Email</span>
                <span className="font-sans">Lớp</span>
              </div>
              <div
                className="flex flex-col gap-y-5"
                style={{ width: "250px", textAlign: "left" }}
              >
                <span>N19DCCN135</span>
                <span>Vũ Thị Hồng Oanh</span>
                <span>Nữ</span>
                <span>18/03/2000</span>
                <span>ấp 5 xã Bom Bo Huyện Bù Đăng tỉnh Bình Phước</span>
                <span>97 Man Thiện Phường Hiệp Phú Q9 TpCHM</span>
                <span>0961319365</span>
                <span>vuthihongoanh1803@gmail.com</span>
                <span>Công nghệ phần mềm - 2019 -1</span>
              </div>
            </div>
          </div>
          <div className="flex items-center justify-center gap-5">
            <button className="w-[96px] h-[36px] bg-[#14238A] text-sm text-white rounded-md">
              Thoát
            </button>
          </div>
        </div>
      </div>
    </ReactModal>
  );
};

export default DetailStudent;
