export const addDepartment = (formData) => async (dispatch) => {
  try {
    const { data } = await api.addDepartment(formData);
    if (data.status === "success") {
      const newDepartment = data.retObj;
      alert("Department added successfully!");
      dispatch({ type: ADD_DEPARTMENT, payload: newDepartment });
    } else {
      const errorMessage = data.message;
      console.log("errorMessage", errorMessage);
      dispatch({ type: SET_ERRORS, payload: errorMessage });
    }
  } catch (error) {
    dispatch({ type: SET_ERRORS, payload: error.response.data.message });
  }
};
