import { useDispatch, useSelector } from "react-redux";
import { getCoursebyKeHoachNam } from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import { SET_ERRORS } from "../../../redux/actionTypes";
import { Link } from "react-router-dom";

// http://localhost:9090/api/admin/dsLopTc/lop/CNTT01
const Body = () => {
  const dispatch = useDispatch();
  const [error, setError] = useState({});
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);

  const store = useSelector((state) => state);

  const [value, setValue] = useState({
    idKeHoachNam: "",
    keySearch: "",
  });

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    console.log("value", value);
    dispatch(
      getCoursebyKeHoachNam({
        params: {
          ...value,
        },
      })
    );
  };

  const courses = useSelector((state) => state.admin.courses);
  console.log("course", courses);

  useEffect(() => {
    if (courses?.length !== 0 || courses?.length === 0) {
      setLoading(false);
    }
  }, [courses]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Danh sách Lớp Tín chỉ</p>

      {/* ô search */}
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Lớp</label>

          <div className="flex">
            <Select
              displayEmpty
              sx={{ height: 36 }}
              inputProps={{ "aria-label": "Without label" }}
              value={value.idKeHoachNam}
              onChange={(e) =>
                setValue({ ...value, idKeHoachNam: e.target.value })
              }
              className={`${classes.InputStyle} hover:focus:border-none `}
            >
              <MenuItem value="">None</MenuItem>
              <MenuItem value="c2b42960">MKH1</MenuItem>
              {/* <MenuItem value="c2b42960">MKH0</MenuItem> */}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
          <div className={classes.WrapInputLabel}>
            <h1 className={classes.LabelStyle}>Tìm môn học *:</h1>
            <input
              placeholder="Tên môn học"
              className={classes.InputStyle}
              type="text"
              value={value.keySearch}
              onChange={(e) =>
                setValue({ ...value, keySearch: e.target.value })
              }
            />
          </div>
        </form>
      </div>

      <div className="w-full">
        <div className="col-span-3">
          <div className={classes.loadingAndError}>
            {loading && courses?.length !== 0 && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#157572"
                messageColor="#157572"
              />
            )}
            {/* chỗ này tạm thời để vậy đã */}
            {courses?.length === 0 && (
              <p className="text-2xl font-bold text-red-500">
                Chưa có lớp tín chỉ nào trong kế hoạch năm này!
              </p>
            )}
          </div>

          {search &&
            !loading &&
            // Object.keys(error).length === 0 &&
            courses?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">Mã Lớp Tín Chỉ</th>
                    <th className="px-4 py-2">Số lượng</th>
                    <th className="px-4 py-2">Số lượng còn</th>
                    <th className="px-4 py-2">Mã Môn Học</th>
                    <th className="px-4 py-2">Mã Giảng Viên</th>
                    <th className="px-4 py-2">Hành Động</th>
                  </tr>
                </thead>
                <tbody className="">
                  {courses?.map((course, idx) => (
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border">{idx + 1}</td>
                      <td className="px-4 py-2 border">{course.maLopTc}</td>
                      <td className="px-4 py-2 border">{course.soLuong}</td>
                      <td className="px-4 py-2 border">{course.soLuongCon}</td>
                      <td className="px-4 py-2 border">{course.maMh}</td>
                      <td className="px-4 py-2 border">{course.maGv}</td>
                      <td className="px-4 py-2 border">
                        <div className="flex flex-col items-center justify-center h-full gap-y-1">
                          <Link to={`/admin/thongkedetail/${course.id}`}>
                            <button className="w-full whitespace-nowrap h-full px-3 py-1 font-bold text-white rounded hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline">
                              Xem thống kê
                            </button>
                          </Link>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>
    </div>
  );
};

export default Body;
