export const addDepartment = (formData) => async (dispatch) => {
  try {
    const { data } = await api.addDepartment(formData);
    if (data.status === "success") {
      alert("Department added successfully!");
      dispatch({ type: ADD_DEPARTMENT, payload: true });
    } else {
      // const errorMessage = data.message;
      console.log("data.message", data.message);

      dispatch({ type: SET_ERRORS, payload: data.message });
    }
  } catch (error) {
    if (
      error.response &&
      error.response.data &&
      error.response.data.status === "error"
    ) {
      dispatch({ type: SET_ERRORS, payload: error.response.data.message });
    } else {
      console.log("Unknown error occurred");
    }
  }
};
