import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  deleteUnit,
  getUnitDepartment,
  updateUnit,
} from "../../../redux/actions/adminActions";
import Select from "@mui/material/Select";
import Spinner from "../../../utils/Spinner";
import * as classes from "../../../utils/styles";
import MenuItem from "@mui/material/MenuItem";
import {
  DELETE_UNIT,
  SET_ERRORS,
  UPDATE_UNIT,
} from "../../../redux/actionTypes";
import { Link } from "react-router-dom";
import ReactModal from "react-modal";
// http://localhost:9090/api/admin/lop/khoa/CNTT

const modalStyles = {
  content: {
    top: "45%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: 0,
  },
};
const Body = () => {
  const dispatch = useDispatch();
  const [department, setDepartment] = useState("");
  const [error, setError] = useState({});
  const departments = useSelector((state) => state.admin.allDepartment);
  const [search, setSearch] = useState(false);
  const [loading, setLoading] = useState(false);
  const store = useSelector((state) => state);

  // begin edit
  const [value, setValue] = useState({
    tenLop: "",
    maLop: "",
    maKhoa: "",
    id: "",
  });
  const [selectedUnit, setSelectedUnit] = useState("");

  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleEditClick = (unit) => {
    setSelectedUnit(unit);
    setIsModalOpen(true);
    setValue({
      tenLop: "",
      maLop: unit.maLop,
      maKhoa: unit.maKhoa,
      id: unit.id,
    });
  };
  const openModal = () => {
    setIsModalOpen(true);
  };
  const closeModal = () => {
    setIsModalOpen(false);
  };
  //modal
  const handleFormSubmit = (e) => {
    e.preventDefault();

    const updatedValue = {};
    if (value.tenLop !== "") {
      updatedValue.tenLop = value.tenLop;
    } else {
      updatedValue.tenLop = selectedUnit.tenLop;
    }
    dispatch(updateUnit({ ...selectedUnit, ...updatedValue }));
    dispatch({ type: UPDATE_UNIT, payload: false });
    closeModal();
  };

  useEffect(() => {
    dispatch(getUnitDepartment(selectedUnit.maKhoa));
  }, [dispatch, store.errors, store.admin.updatedUnit]);

  // END EDIT

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setError(store.errors);
      setLoading(false);
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSearch(true);
    setLoading(true);
    setError({});
    const departmentObj = departments.find((dp) => dp.tenKhoa === department);
    const departmentId = departmentObj.maKhoa;
    dispatch(getUnitDepartment(departmentId));
  };

  const units = useSelector((state) => state.admin.units.retObj);

  useEffect(() => {
    if (units?.length !== 0 || units?.length === 0) {
      setLoading(false);
    }
  }, [units]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);

  // xóa

  const [checkedValue, setCheckedValue] = useState([]);

  // check xóa
  // const handleInputChange = (e) => {
  //   const tempCheck = checkedValue;
  //   let index;
  //   if (e.target.checked) {
  //     tempCheck.push(e.target.value);
  //   } else {
  //     index = tempCheck.indexOf(e.target.value);
  //     tempCheck.splice(index, 1);
  //   }
  //   setCheckedValue(tempCheck);
  // };
  const handleInputChange = (e) => {
    const tempCheck = checkedValue.slice(); // sử dụng slice() để sao chép mảng
    let index;
    if (e.target.checked) {
      tempCheck.push(e.target.value);
    } else {
      index = tempCheck.indexOf(e.target.value);
      tempCheck.splice(index, 1);
    }
    setCheckedValue([...tempCheck]); // sử dụng toán tử spread để truyền vào một mảng mới
  };
  // hàm xóa
  // BEGIN DELETE

  const dltSubject = (e) => {
    dispatch(deleteUnit(checkedValue));
  };
  useEffect(() => {
    if (store.admin.unitDeleted) {
      setLoading(false);
      setCheckedValue([]);
      const departmentObj = departments.find((dp) => dp.tenKhoa === department);
      const departmentId = departmentObj.maKhoa;
      dispatch(getUnitDepartment(departmentId));
      dispatch({ type: DELETE_UNIT, payload: false });
    }
  }, [store.admin.unitDeleted]);

  //END DELETE

  return (
    <div className="flex-[0.8] mt-3 mx-5 item-center">
      <p className="block text-black ">Danh sách Lớp</p>

      <div className="flex mt-4">
        <Link to="/admin/addunit" className="btn btn-primary">
          <button
            className="items-center gap-[9px] mr-4 w-[88px] h-[53px] hover:bg-[#04605E] block py-2 font-bold text-white rounded-lg px-4 
           bg-[#157572] focus:outline-none focus:shadow-outline "
          >
            Thêm
          </button>
        </Link>

        {units && units?.length !== 0 && (
          <button
            onClick={dltSubject}
            className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
          >
            Xóa
          </button>
        )}

        {departments && checkedValue?.length > 0 ? (
          <button
            onClick={dltSubject}
            className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
          >
            Xóa
          </button>
        ) : (
          <button
            onClick={dltSubject}
            className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4"
            disabled
          >
            Xóa
          </button>
        )}
        {/* <button
          onClick={dltSubject}
          className="items-center  gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4  hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
        >
          Xóa
        </button> */}
      </div>

      {/* ô search */}
      <div className="items-center my-8 mt-2 mb-2 rounded-lg">
        <form
          className="flex flex-col col-span-1 space-y-2"
          onSubmit={handleSubmit}
        >
          <label htmlFor="department">Khoa</label>

          <div className="flex">
            <Select
              required
              displayEmpty
              sx={{ height: 36, width: 224 }}
              inputProps={{ "aria-label": "Without label" }}
              value={department}
              onChange={(e) => setDepartment(e.target.value)}
            >
              <MenuItem value="">None</MenuItem>
              {departments?.map((dp, idx) => (
                <MenuItem key={idx} value={dp.tenKhoa}>
                  {dp.tenKhoa}
                </MenuItem>
              ))}
            </Select>
            <button
              className={`${classes.adminFormSubmitButton} w-56 ml-3`}
              type="submit"
            >
              Search
            </button>
          </div>
        </form>
      </div>

      <div className="w-full">
        <div className="col-span-3">
          <div className={classes.loadingAndError}>
            {/* chỗ này là bug đó nhưng thôi lệ lấp liếm đi vậy, 001s thôi mà, làm gì căng */}
            {loading && units?.length !== 0 && (
              <Spinner
                message="Loading"
                height={50}
                width={150}
                color="#111111"
                messageColor="blue"
              />
            )}

            {/* chỗ này tạm thời để vậy đã */}
            {units?.length === 0 && (
              <p className="text-2xl font-bold text-red-500">
                Khoa chưa có lớp
              </p>
            )}
          </div>

          {search &&
            !loading &&
            Object.keys(error).length === 0 &&
            units?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">Chọn</th>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">Mã Lớp</th>
                    <th className="px-4 py-2">Tên Lớp</th>
                    <th className="px-4 py-2">Hành động</th>
                  </tr>
                </thead>
                <tbody className="">
                  {units?.map((unit, idx) => (
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border">
                        <input
                          onChange={handleInputChange}
                          value={unit.id}
                          type="checkbox"
                        />
                      </td>
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">{unit.maLop}</td>
                      <td className="px-4 py-2 border">{unit.tenLop}</td>
                      <td
                        className="items-center justify-center px-4 py-2 mr-0 border"
                        style={{ display: "flex", justifyContent: "center" }}
                      >
                        <button
                          className="px-3.5 py-1 font-bold text-white rounded hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                          onClick={() => handleEditClick(unit)}
                        >
                          Sửa
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
        </div>
      </div>

      {selectedUnit ? (
        <ReactModal
          isOpen={isModalOpen}
          onRequestClose={openModal}
          style={modalStyles}
          ariaHideApp={false}
        >
          <div className="flex flex-col h-full bg-white rounded-xl">
            <form
              className="min-w-[685px] min-h-[300px] py-7 px-7 text-center bg-[#fff]  rounded-md  shadow-md mx-auto"
              onSubmit={handleFormSubmit}
            >
              <div className="grid grid-cols-2 gap-x-10">
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Mã Lớp :</h1>
                  <input
                    placeholder={selectedUnit?.maLop}
                    disabled
                    className={classes.InputStyle}
                    type="text"
                    value={value.maLop}
                  />
                </div>

                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Tên Lớp :</h1>
                  <input
                    placeholder={selectedUnit?.tenLop}
                    className={classes.InputStyle}
                    type="text"
                    value={value.tenLop}
                    onChange={(e) =>
                      setValue({
                        ...value,
                        tenLop: e.target.value,
                      })
                    }
                  />
                </div>
              </div>

              <div className="flex items-center justify-center mt-10 space-x-6">
                <button className={classes.adminFormSubmitButton} type="submit">
                  Lưu
                </button>
                <Link to="/admin/allUnit" className="btn btn-primary">
                  <button
                    className={classes.adminFormClearButton}
                    type="button"
                    onClick={closeModal}
                  >
                    Thoát
                  </button>
                </Link>
              </div>
            </form>
          </div>
        </ReactModal>
      ) : null}
    </div>
  );
};

export default Body;
