import React, { useEffect, useState } from "react";
import ReactModal from "react-modal";
import * as classes from "../../../utils/styles";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import {
  addCourseDetail,
  getAllCourseDetailCourse,
} from "../../../redux/actions/adminActions";
import Spinner from "../../../utils/Spinner";
import { ADD_COURSE_DETAIL, SET_ERRORS } from "../../../redux/actionTypes";

const modalStyles = {
  content: {
    top: "50%",
    left: "55%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "0",
  },
};

const CourseDetail = ({ course, isOpen, onClose }) => {
  console.log("runing...1");

  const { maLopTc } = course;
  console.log("maLopTc", maLopTc);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getAllCourseDetailCourse(maLopTc));
  }, [dispatch]);

  const coursedetails = useSelector((state) => state.admin.coursedetails);
  const store = useSelector((state) => state);
  const [isloading, setIsLoading] = useState(false);
  const [derror, setDerror] = useState({});

  const [valueCourseDetail, setValueCourseDetail] = useState({
    tiet: "",
    thu: "",
    soTiet: "",
    phong: "",
    maLopTc: maLopTc,
  });

  useEffect(() => {
    if (Object.keys(store.errors).length !== 0) {
      setDerror(store.errors);
      setValueCourseDetail({ ...valueCourseDetail, maLopTc: "" });
    }
  }, [store.errors]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setDerror({});
    setIsLoading(true);
    dispatch(addCourseDetail(valueCourseDetail));
  };

  useEffect(() => {
    if (store.errors || store.admin.coursedetailAdded) {
      setIsLoading(false);
      if (store.admin.coursedetailAdded) {
        setValueCourseDetail({
          tiet: "",
          thu: "",
          soTiet: "",
          phong: "",
          maLopTc: "",
        });
        dispatch({ type: SET_ERRORS, payload: {} });
        dispatch({ type: ADD_COURSE_DETAIL, payload: false });
      }
    } else {
      setIsLoading(true);
    }
  }, [store.errors, store.admin.coursedetailAdded]);

  useEffect(() => {
    dispatch({ type: SET_ERRORS, payload: {} });
  }, []);
  return (
    <ReactModal
      style={modalStyles}
      ariaHideApp={false}
      isOpen={isOpen}
      onRequestClose={onClose}
      overlayClassName="overlay"
      shouldCloseOnOverlayClick={true}
    >
      <div className="flex-[0.8] mt-3 mx-5 item-center w-[1100x] h-[600px]">
        <p className="block text-black ">Thêm chi tiết lớp tín chỉ</p>

        <div className="flex mt-4">
          {/* <button
              className="items-center gap-[9px] mr-4 w-[88px] h-[53px] hover:bg-[#04605E] block py-2 font-bold text-white rounded-lg px-4 
           bg-[#157572] focus:outline-none focus:shadow-outline "
            >
              Thêm
            </button> */}

          {/* {coursedetails && coursedetails.length !== 0 && (
          <button
            onClick={dltSubject}
            className={
              "items-center gap-[9px] mr-4 w-[88px] h-[53px] block py-2 font-bold text-[#7D1711] bg-[#FDD1D1] border border: 1.11647px solid #FD9999 rounded-lg px-4" +
              (checkedValue && checkedValue.length
                ? " hover:bg-[#FD9999] focus:#FD9999 focus:shadow-outline"
                : "")
            }
            disabled={!(units && checkedValue?.length > 0)}
          >
            Xóa
          </button>
        )} */}
        </div>

        <div className={classes.Form1}>
          <form
            className="w-full min-h-[120px] py-8 px-7 text-center bg-[#fff] border rounded-md  shadow-md mx-auto"
            onSubmit={handleSubmit}
          >
            <div className="grid grid-cols-5 gap-x-5">
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Mã Lớp Tín Chỉ :</h1>

                <input
                  placeholder={maLopTc}
                  required
                  className={classes.InputStyle}
                  type="text"
                  disabled
                  value={valueCourseDetail.maLopTc}
                  onChange={(e) =>
                    setValueCourseDetail({
                      ...valueCourseDetail,
                      maLopTc: maLopTc,
                    })
                  }
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Tiết *:</h1>

                <input
                  placeholder="Tiết"
                  required
                  className={classes.InputStyle}
                  type="text"
                  value={valueCourseDetail.tiet}
                  onChange={(e) =>
                    setValueCourseDetail({
                      ...valueCourseDetail,
                      tiet: e.target.value,
                    })
                  }
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Thứ *:</h1>

                <input
                  placeholder="Thứ"
                  required
                  className={classes.InputStyle}
                  type="text"
                  value={valueCourseDetail.thu}
                  onChange={(e) =>
                    setValueCourseDetail({
                      ...valueCourseDetail,
                      thu: e.target.value,
                    })
                  }
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Số tiết *:</h1>

                <input
                  placeholder="Số tiết"
                  required
                  className={classes.InputStyle}
                  type="text"
                  value={valueCourseDetail.soTiet}
                  onChange={(e) =>
                    setValueCourseDetail({
                      ...valueCourseDetail,
                      soTiet: e.target.value,
                    })
                  }
                />
              </div>
              <div className={classes.WrapInputLabel}>
                <h1 className={classes.LabelStyle}>Phòng *:</h1>

                <input
                  placeholder="Phòng"
                  required
                  className={classes.InputStyle}
                  type="text"
                  value={valueCourseDetail.phong}
                  onChange={(e) =>
                    setValueCourseDetail({
                      ...valueCourseDetail,
                      phong: e.target.value,
                    })
                  }
                />
              </div>
            </div>

            <div className="flex items-center justify-center mt-10 space-x-6">
              <button className={classes.adminFormSubmitButton} type="submit">
                Gửi
              </button>
              <button
                onClick={() => {
                  setValueCourseDetail({
                    tiet: "",
                    thu: "",
                    soTiet: "",
                    phong: "",
                    maLopTc: "",
                  });
                  setDerror({});
                }}
                className={classes.adminFormClearButton}
                type="button"
              >
                Xóa
              </button>
            </div>
            <div className={classes.loadingAndError}>
              {isloading && (
                <Spinner
                  message="Đang thêm chi tiết lớp tín chỉ..."
                  height={30}
                  width={150}
                  color="#157572"
                  messageColor="157572"
                />
              )}

              {derror.message ? (
                <p className="text-red-500">{derror.message}</p>
              ) : null}
            </div>
          </form>
        </div>
        <p className="block mt-2 text-black">Danh sách chi tiết lớp tín chỉ</p>

        <div className="w-full">
          <div className="col-span-3">
            <div className={classes.loadingAndError}>
              {isloading && coursedetails?.length !== 0 && (
                <Spinner
                  message="Loading"
                  height={50}
                  width={150}
                  color="#157572"
                  messageColor="#157572"
                />
              )}

              {coursedetails?.length === 0 && (
                <p className="text-2xl font-bold text-red-500">
                  lớp tín chỉ chưa có chi tiết lớp tín chỉ nghe quắn não qué
                </p>
              )}
            </div>

            {!isloading && coursedetails?.length !== 0 && (
              <table className="w-full table-auto">
                <thead className="bg-[#E1EEEE] items-center">
                  <tr>
                    <th className="px-4 py-2">Chọn</th>
                    <th className="px-4 py-2">STT</th>
                    <th className="px-4 py-2">Mã lớp tín chỉ</th>
                    <th className="px-4 py-2">Tiết</th>
                    <th className="px-4 py-2">Phòng</th>
                    <th className="px-4 py-2">Số tiết</th>
                    <th className="px-4 py-2">Thứ</th>
                    <th className="px-4 py-2">hành động</th>
                  </tr>
                </thead>
                <tbody className="">
                  {coursedetails?.map((coursedetail, idx) => (
                    <tr
                      className="justify-center item-center hover:bg-[#EEF5F5]"
                      key={idx}
                    >
                      <td className="px-4 py-2 border">
                        <input
                          // onChange={handleInputChange}
                          // checked={checkedValue.includes(coursedetail.id)}
                          // value={coursedetail.id}
                          type="checkbox"
                        />
                      </td>
                      <td className="px-4 py-2 border">{idx + 1}</td>

                      <td className="px-4 py-2 border">
                        {coursedetail.maLopTc}
                      </td>
                      <td className="px-4 py-2 border">{coursedetail.tiet}</td>
                      <td className="px-4 py-2 border">{coursedetail.phong}</td>
                      <td className="px-4 py-2 border">
                        {coursedetail.soTiet}
                      </td>
                      <td className="px-4 py-2 border">{coursedetail.thu}</td>

                      <td
                        className="items-center justify-center px-4 py-2 mr-0 border"
                        style={{ display: "flex", justifyContent: "center" }}
                      >
                        <button
                          className="px-3.5 py-1 font-bold text-white rounded hover:bg-[#04605E] bg-[#157572] focus:outline-none focus:shadow-outline"
                          // onClick={() => handleEditClick(coursedetail)}
                        >
                          Sửa
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
        {/* edit */}
        {/* {selectedUnit ? (
        <ReactModal
          isOpen={isModalOpen}
          onRequestClose={openModal}
          style={modalStyles}
          ariaHideApp={false}
        >
          <div className="flex flex-col h-full bg-white rounded-xl">
            <form
              className="min-w-[685px] min-h-[300px] py-7 px-7 text-center bg-[#fff]  rounded-md  shadow-md mx-auto"
              onSubmit={handleFormSubmit}
            >
              <div className="grid grid-cols-2 gap-x-10">
                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Mã Lớp :</h1>
                  <input
                    placeholder={selectedUnit?.maLop}
                    disabled
                    className={classes.InputStyle}
                    type="text"
                    value={value.maLop}
                  />
                </div>

                <div className={classes.WrapInputLabel}>
                  <h1 className={classes.LabelStyle}>Tên Lớp :</h1>
                  <input
                    placeholder={selectedUnit?.tenLop}
                    className={classes.InputStyle}
                    type="text"
                    value={value.tenLop}
                    onChange={(e) =>
                      setValueCourseDetail({
                        ...value,
                        tenLop: e.target.value,
                      })
                    }
                  />
                </div>
              </div>

              <div className="flex items-center justify-center mt-10 space-x-6">
                <button className={classes.adminFormSubmitButton} type="submit">
                  Lưu
                </button>
                <Link to="/admin/allUnit" className="btn btn-primary">
                  <button
                    className={classes.adminFormClearButton}
                    type="button"
                    onClick={closeModal}
                  >
                    Thoát
                  </button>
                </Link>
              </div>
            </form>
          </div>
        </ReactModal>
      ) : null} */}
        <div className="flex items-center justify-center gap-5">
          <button
            className={classes.adminFormClearButton}
            type="button"
            onClick={onClose}
          >
            Thoát
          </button>
        </div>
      </div>
    </ReactModal>
  );
};

export default CourseDetail;
