import React, { useState } from "react";
import HomeIcon from "@mui/icons-material/Home";
import Calendar from "react-calendar";
import EngineeringIcon from "@mui/icons-material/Engineering";
import BoyIcon from "@mui/icons-material/Boy";
import SupervisorAccountIcon from "@mui/icons-material/SupervisorAccount";
import MenuBookIcon from "@mui/icons-material/MenuBook";
import "react-calendar/dist/Calendar.css";
import { useSelector } from "react-redux";
import ReplyIcon from "@mui/icons-material/Reply";
const Body = () => {
  const [open, setOpen] = useState(false);
  const [value, onChange] = useState(new Date());
  const students = useSelector((state) => state.admin.allStudent);
  const units = useSelector((state) => state.admin.allUnit);

  const teachers = useSelector((state) => state.admin.allTeacher);
  const departments = useSelector((state) => state.admin.allDepartment);

  return (
    <div className="flex-[0.8] mt-3 mx-5">
      <div className="space-y-5">
        <div className="flex items-center space-x-2 text-gray-400">
          <HomeIcon />
          <h1>Dashboard</h1>
        </div>

        <div className="flex flex-col space-y-4 overflow-y-hidden">
          <div className="bg-white h-[8rem] rounded-xl shadow-lg grid grid-cols-4 justify-between px-8 items-center space-x-4">
            <div className="flex items-center space-x-4 border-r-2">
              <EngineeringIcon
                className="py-2 bg-orange-300 rounded-full"
                sx={{ fontSize: 40 }}
              />
              <div className="flex flex-col">
                <h1>Teacher</h1>
                <h2 className="text-2xl font-bold">{teachers?.length}</h2>
              </div>
            </div>
            <div className="flex items-center space-x-4 border-r-2">
              <BoyIcon
                className="py-2 bg-orange-300 rounded-full"
                sx={{ fontSize: 40 }}
              />
              <div className="flex flex-col">
                <h1>Student</h1>
                <h2 className="text-2xl font-bold">{students?.length}</h2>
              </div>
            </div>
            <div className="flex items-center space-x-4 border-r-2">
              <SupervisorAccountIcon
                className="py-2 bg-orange-300 rounded-full"
                sx={{ fontSize: 40 }}
              />
              <div className="flex flex-col">
                <h1>Lớp</h1>
                <h2 className="text-2xl font-bold">{units?.length}</h2>
              </div>
            </div>
            <div className="flex items-center space-x-4 ">
              <MenuBookIcon
                className="py-2 bg-orange-300 rounded-full"
                sx={{ fontSize: 40 }}
              />
              <div className="flex flex-col">
                <h1>Department</h1>
                <h2 className="text-2xl font-bold">{departments?.length}</h2>
              </div>
            </div>
          </div>
          <div className="flex space-x-4">
            <div className="flex flex-col w-2/6 space-y-4">
              <div className="bg-white h-[17rem] rounded-xl shadow-lg">
                <Calendar onChange={onChange} value={value} />
              </div>
            </div>
            <div className="bg-white h-[17rem] w-full rounded-xl shadow-lg flex flex-col  pt-3">
              <div className="flex px-3">
                {open && (
                  <ReplyIcon
                    onClick={() => setOpen(false)}
                    className="cursor-pointer"
                  />
                )}
                <h1 className="w-full text-xl font-bold text-center">
                  Thống kê giới tính nghỉ học các kiểu
                </h1>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Body;
